mapper.select:
SELECT  
	substring(scripture_no, 2) AS scripture_no 
FROM
SCRIPTURE

	
		type = #{type}
	
	
		AND create_date = #{createDate}

SELECT SUBSTRING(scripture_no, 2) + 0 AS scriptureNo, create_date AS last_create_date FROM SCRIPTURE
type = #{type}
ORDER BY scriptureNo DESC LIMIT 0, 1
