INSERT INTO `scripture` VALUES ('A1', '【帖前3:3】免得有人被诸般患难摇动，因为你们自己知道我们受患难原是命定的。', null, '2017-01-01', null, 'A');
INSERT INTO `scripture` VALUES ('A10', '【提前2:1-2】1我劝你第一要为万人恳求、祷告、代求、祝谢，2为君王和一切在位的，也该如此，使我们可以敬虔、端正、平安无事地度日。', null, '2017-01-10', null, 'A');
INSERT INTO `scripture` VALUES ('A100', '【犹3】亲爱的弟兄啊，我想尽心写信给你们，论我们同得救恩的时候，就不得不写信劝你们，要为从前一次交付圣徒的真道竭力地争辩。', null, '2017-04-10', null, 'A');
INSERT INTO `scripture` VALUES ('A101', '【犹20-21】20亲爱的弟兄啊，你们却要在至圣的真道上造就自己，在圣灵里祷告，21保守自己常在神的爱中，仰望我们主耶稣基督的怜悯，直到永生。', null, '2017-04-11', null, 'A');
INSERT INTO `scripture` VALUES ('A102', '【启1:8】主神说：“我是阿拉法，我是俄梅戛（“阿拉法，俄梅戛”乃希腊字母首末二字），是昔在、今在、以后永在的全能者。”', null, '2017-04-12', null, 'A');
INSERT INTO `scripture` VALUES ('A103', '【启2:4】然而，有一件事我要责备你，就是你把起初的爱心离弃了。', null, '2017-04-13', null, 'A');
INSERT INTO `scripture` VALUES ('A104', '【启3:5】凡得胜的，必这样穿白衣，我也必不从生命册上涂抹他的名，且要在我父面前和我父众使者面前认他的名。', null, '2017-04-14', null, 'A');
INSERT INTO `scripture` VALUES ('A105', '【启3:16】你既如温水，也不冷也不热，所以我必从我口中把你吐出去。', null, '2017-04-15', null, 'A');
INSERT INTO `scripture` VALUES ('A106', '【启3:20】看哪，我站在门外叩门，若有听见我声音就开门的，我要进到他那里去，我与他，他与我，一同坐席。', null, '2017-04-16', null, 'A');
INSERT INTO `scripture` VALUES ('A107', '【启5:12】大声说：“曾被杀的羔羊是配得权柄、丰富、智慧、能力、尊贵、荣耀、颂赞的。”', null, '2017-04-17', null, 'A');
INSERT INTO `scripture` VALUES ('A108', '【启7:16-17】16他们不再饥、不再渴，日头和炎热也必不再伤害他们，17因为宝座中的羔羊必牧养他们，领他们到生命水的泉源，神也必擦去他们一切的眼泪。”', null, '2017-04-18', null, 'A');
INSERT INTO `scripture` VALUES ('A109', '【启11:15】第七位天使吹号，天上就有大声音说：“世上的国成了我主和主基督的国；他要作王，直到永永远远。”', null, '2017-04-19', null, 'A');
INSERT INTO `scripture` VALUES ('A11', '【提前2:9-10】9又愿女人廉耻、自守，以正派衣裳为妆饰，不以编发、黄金、珍珠和贵价的衣裳为妆饰，10只要有善行，这才与自称是敬神的女人相宜。', null, '2017-01-11', null, 'A');
INSERT INTO `scripture` VALUES ('A110', '【启12:11】弟兄胜过它，是因羔羊的血和自己所见证的道。他们虽至于死，也不爱惜性命。', null, '2017-04-20', null, 'A');
INSERT INTO `scripture` VALUES ('A111', '【启20:15】若有人名字没记在生命册上，他就被扔在火湖里。', null, '2017-04-21', null, 'A');
INSERT INTO `scripture` VALUES ('A112', '【启21:1】我又看见一个新天新地，因为先前的天地已经过去了，海也不再有了。', null, '2017-04-22', null, 'A');
INSERT INTO `scripture` VALUES ('A113', '【启21:2】我又看见圣城新耶路撒冷由神那里从天而降，预备好了，就如新妇妆饰整齐，等候丈夫。', null, '2017-04-23', null, 'A');
INSERT INTO `scripture` VALUES ('A114', '【启21:3】我听见有大声音从宝座出来说：“看哪，神的帐幕在人间。他要与人同住，他们要作他的子民；神要亲自与他们同在，作他们的神。', null, '2017-04-24', null, 'A');
INSERT INTO `scripture` VALUES ('A115', '【启21:4】神要擦去他们一切的眼泪，不再有死亡，也不再有悲哀、哭号、疼痛，因为以前的事都过去了。”', null, '2017-04-25', null, 'A');
INSERT INTO `scripture` VALUES ('A116', '【启21:5】坐宝座的说：“看哪，我将一切都更新了。”又说：“你要写上，因这些话是可信的，是真实的。”', null, '2017-04-26', null, 'A');
INSERT INTO `scripture` VALUES ('A117', '【启21:6】他又对我说：“都成了！我是阿拉法，我是俄梅戛；我是初，我是终。我要将生命泉的水白白赐给那口渴的人喝。', null, '2017-04-27', null, 'A');
INSERT INTO `scripture` VALUES ('A118', '【启21:7】得胜的，必承受这些为业。我要作他的神，他要作我的儿子。', null, '2017-04-28', null, 'A');
INSERT INTO `scripture` VALUES ('A119', '【启21:8】惟有胆怯的、不信的、可憎的、杀人的、淫乱的、行邪术的、拜偶像的和一切说谎话的，他们的份就在烧着硫磺的火湖里，这是第二次的死。”', null, '2017-04-29', null, 'A');
INSERT INTO `scripture` VALUES ('A12', '【提前4:8】操练身体，益处还少；惟独敬虔，凡事都有益处，因有今生和来生的应许。', null, '2017-01-12', null, 'A');
INSERT INTO `scripture` VALUES ('A120', '【启22:7】‘看哪，我必快来。凡遵守这书上预言的有福了！’”', null, '2017-04-30', null, 'A');
INSERT INTO `scripture` VALUES ('A121', '【启22:12】“看哪，我必快来。赏罚在我，要照各人所行的报应他。', null, '2017-05-01', null, 'A');
INSERT INTO `scripture` VALUES ('A122', '【启22:20】证明这事的说：“是了，我必快来。”阿们！主耶稣啊，我愿你来！', null, '2017-05-02', null, 'A');
INSERT INTO `scripture` VALUES ('A123', '【太1:21】她将要生一个儿子，你要给他起名叫耶稣，因他要将自己的百姓从罪恶里救出来。”', null, '2017-05-03', null, 'A');
INSERT INTO `scripture` VALUES ('A124', '【太1:23】说：“必有童女怀孕生子，人要称他的名为以马内利。”（“以马内利”翻出来就是“神与我们同在”。）', null, '2017-05-04', null, 'A');
INSERT INTO `scripture` VALUES ('A125', '【太3:8】你们要结出果子来，与悔改的心相称。', null, '2017-05-05', null, 'A');
INSERT INTO `scripture` VALUES ('A126', '【太3:16-17】16耶稣受了洗，随即从水里上来。天忽然为他开了，他就看见神的灵仿佛鸽子降下，落在他身上。17从天上有声音说：“这是我的爱子，我所喜悦的。”', null, '2017-05-06', null, 'A');
INSERT INTO `scripture` VALUES ('A127', '【太4:4】耶稣却回答说：“经上记着说：‘人活着，不是单靠食物，乃是靠神口里所出的一切话。’”', null, '2017-05-07', null, 'A');
INSERT INTO `scripture` VALUES ('A128', '【太4:17】从那时候，耶稣就传起道来，说：“天国近了，你们应当悔改！”', null, '2017-05-08', null, 'A');
INSERT INTO `scripture` VALUES ('A129', '【太4:19-20】19耶稣对他们说：“来跟从我！我要叫你们得人如得鱼一样。”20他们就立刻舍了网，跟从了他。', null, '2017-05-09', null, 'A');
INSERT INTO `scripture` VALUES ('A13', '【提前4:12】不可叫人小看你年轻，总要在言语、行为、爱心、信心、清洁上，都作信徒的榜样。', null, '2017-01-13', null, 'A');
INSERT INTO `scripture` VALUES ('A130', '【太5:3】“虚心的人有福了，因为天国是他们的。', null, '2017-05-10', null, 'A');
INSERT INTO `scripture` VALUES ('A131', '【太5:4】哀恸的人有福了，因为他们必得安慰。', null, '2017-05-11', null, 'A');
INSERT INTO `scripture` VALUES ('A132', '【太5:5】温柔的人有福了，因为他们必承受地土。', null, '2017-05-12', null, 'A');
INSERT INTO `scripture` VALUES ('A133', '【太5:6】饥渴慕义的人有福了，因为他们必得饱足。', null, '2017-05-13', null, 'A');
INSERT INTO `scripture` VALUES ('A134', '【太5:7】怜恤人的人有福了，因为他们必蒙怜恤。', null, '2017-05-14', null, 'A');
INSERT INTO `scripture` VALUES ('A135', '【太5:8】清心的人有福了，因为他们必得见神。', null, '2017-05-15', null, 'A');
INSERT INTO `scripture` VALUES ('A136', '【太5:9】使人和睦的人有福了，因为他们必称为神的儿子。', null, '2017-05-16', null, 'A');
INSERT INTO `scripture` VALUES ('A137', '【太5:10】为义受逼迫的人有福了，因为天国是他们的。', null, '2017-05-17', null, 'A');
INSERT INTO `scripture` VALUES ('A138', '【太5:13】“你们是世上的盐。盐若失了味，怎能叫它再咸呢？以后无用，不过丢在外面，被人践踏了。', null, '2017-05-18', null, 'A');
INSERT INTO `scripture` VALUES ('A139', '【太5:14】你们是世上的光。城造在山上，是不能隐藏的。', null, '2017-05-19', null, 'A');
INSERT INTO `scripture` VALUES ('A14', '【提前6:6-8】6然而，敬虔加上知足的心便是大利了，7因为我们没有带什么到世上来，也不能带什么去，8只要有衣有食，就当知足。', null, '2017-01-14', null, 'A');
INSERT INTO `scripture` VALUES ('A140', '【太5:15】人点灯，不放在斗底下，是放在灯台上，就照亮一家的人。', null, '2017-05-20', null, 'A');
INSERT INTO `scripture` VALUES ('A141', '【太5:16】你们的光也当这样照在人前，叫他们看见你们的好行为，便将荣耀归给你们在天上的父。”', null, '2017-05-21', null, 'A');
INSERT INTO `scripture` VALUES ('A142', '【太5:17】“莫想我来要废掉律法和先知；我来不是要废掉，乃是要成全。', null, '2017-05-22', null, 'A');
INSERT INTO `scripture` VALUES ('A143', '【太5:18】我实在告诉你们：就是到天地都废去了，律法的一点一画也不能废去，都要成全。', null, '2017-05-23', null, 'A');
INSERT INTO `scripture` VALUES ('A144', '【太5:20】我告诉你们：你们的义若不胜于文士和法利赛人的义，断不能进天国。”', null, '2017-05-24', null, 'A');
INSERT INTO `scripture` VALUES ('A145', '【太5:22】只是我告诉你们：凡向弟兄动怒的，难免受审判（有古卷在“凡”字下添“无缘无故地”五字）。凡骂弟兄是拉加的，难免公会的审断；凡骂弟兄是魔利的，难免地狱的火。', null, '2017-05-25', null, 'A');
INSERT INTO `scripture` VALUES ('A146', '【太5:23-24】23所以，你在祭坛上献礼物的时候，若想起弟兄向你怀怨，24就把礼物留在坛前，先去同弟兄和好，然后来献礼物。', null, '2017-05-26', null, 'A');
INSERT INTO `scripture` VALUES ('A147', '【太5:28】只是我告诉你们：凡看见妇女就动淫念的，这人心里已经与她犯奸淫了。', null, '2017-05-27', null, 'A');
INSERT INTO `scripture` VALUES ('A148', '【太5:32】只是我告诉你们：凡休妻的，若不是为淫乱的缘故，就是叫她作淫妇了。人若娶这被休的妇人，也是犯奸淫了。”', null, '2017-05-28', null, 'A');
INSERT INTO `scripture` VALUES ('A149', '【太5:37】你们的话，是，就说是；不是，就说不是；若再多说，就是出于那恶者（或作“是从恶里出来的”）。”', null, '2017-05-29', null, 'A');
INSERT INTO `scripture` VALUES ('A15', '【提前6:10】贪财是万恶之根。有人贪恋钱财，就被引诱离了真道，用许多愁苦把自己刺透了。', null, '2017-01-15', null, 'A');
INSERT INTO `scripture` VALUES ('A150', '【太5:44】只是我告诉你们：要爱你们的仇敌，为那逼迫你们的祷告。', null, '2017-05-30', null, 'A');
INSERT INTO `scripture` VALUES ('A151', '【太6:3-4】3你施舍的时候，不要叫左手知道右手所作的；4要叫你施舍的事行在暗中，你父在暗中察看，必然报答你（有古卷作“必在明处报答你”）。”', null, '2017-05-31', null, 'A');
INSERT INTO `scripture` VALUES ('A152', '【太6:6】你祷告的时候，要进你的内屋，关上门，祷告你在暗中的父。你父在暗中察看，必然报答你。', null, '2017-06-01', null, 'A');
INSERT INTO `scripture` VALUES ('A153', '【太6:7】你们祷告，不可像外邦人，用许多重复话，他们以为话多了必蒙垂听。', null, '2017-06-02', null, 'A');
INSERT INTO `scripture` VALUES ('A154', '【太6:8】你们不可效法他们，因为你们没有祈求以先，你们所需用的，你们的父早已知道了。”', null, '2017-06-03', null, 'A');
INSERT INTO `scripture` VALUES ('A155', '【太6:9】“所以，你们祷告要这样说：‘我们在天上的父，愿人都尊你的名为圣。', null, '2017-06-04', null, 'A');
INSERT INTO `scripture` VALUES ('A156', '【太6:10】愿你的国降临。愿你的旨意行在地上，如同行在天上。', null, '2017-06-05', null, 'A');
INSERT INTO `scripture` VALUES ('A157', '【太6:11】我们日用的饮食，今日赐给我们。', null, '2017-06-06', null, 'A');
INSERT INTO `scripture` VALUES ('A158', '【太6:12】免我们的债，如同我们免了人的债。', null, '2017-06-07', null, 'A');
INSERT INTO `scripture` VALUES ('A159', '【太6:13】不叫我们遇见试探，救我们脱离凶恶（或作“脱离恶者”）。因为国度、权柄、荣耀，全是你的，直到永远。阿们（有古卷无“因为”至“阿们”等字）。’”', null, '2017-06-08', null, 'A');
INSERT INTO `scripture` VALUES ('A16', '【提后1:6-7】6为此我提醒你，使你将神藉我按手所给你的恩赐，再如火挑旺起来。7因为神赐给我们不是胆怯的心，乃是刚强、仁爱、谨守的心。', null, '2017-01-16', null, 'A');
INSERT INTO `scripture` VALUES ('A160', '【太6:14-15】14“你们饶恕人的过犯，你们的天父也必饶恕你们的过犯；15你们不饶恕人的过犯，你们的天父也必不饶恕你们的过犯。”', null, '2017-06-09', null, 'A');
INSERT INTO `scripture` VALUES ('A161', '【太6:17-18】17你禁食的时候，要梳头洗脸，18不叫人看出你禁食来，只叫你暗中的父看见。你父在暗中察看，必然报答你。”', null, '2017-06-10', null, 'A');
INSERT INTO `scripture` VALUES ('A162', '【太6:19-21】19“不要为自己积攒财宝在地上，地上有虫子咬，能锈坏，也有贼挖窟窿来偷；20只要积攒财宝在天上，天上没有虫子咬，不能锈坏，也没有贼挖窟窿来偷。21因为你的财宝在哪里，你的心也在那里。”', null, '2017-06-11', null, 'A');
INSERT INTO `scripture` VALUES ('A163', '【太6:22-23】22“眼睛就是身上的灯。你的眼睛若了亮，全身就光明；23你的眼睛若昏花，全身就黑暗。你里头的光若黑暗了，那黑暗是何等大呢！”', null, '2017-06-12', null, 'A');
INSERT INTO `scripture` VALUES ('A164', '【太6:24】“一个人不能侍奉两个主。不是恶这个爱那个，就是重这个轻那个。你们不能又侍奉神，又侍奉玛门（“玛门”是“财利”的意思）。”', null, '2017-06-13', null, 'A');
INSERT INTO `scripture` VALUES ('A165', '【太6:33-34】33你们要先求他的国和他的义，这些东西都要加给你们了。34所以，不要为明天忧虑，因为明天自有明天的忧虑；一天的难处一天当就够了。”', null, '2017-06-14', null, 'A');
INSERT INTO `scripture` VALUES ('A166', '【太7:1-2】1“你们不要论断人，免得你们被论断。2因为你们怎样论断人，也必怎样被论断；你们用什么量器量给人，也必用什么量器量给你们。', null, '2017-06-15', null, 'A');
INSERT INTO `scripture` VALUES ('A167', '【太7:3-4】3为什么看见你弟兄眼中有刺，却不想自己眼中有梁木呢？4你自己眼中有梁木，怎能对你弟兄说‘容我去掉你眼中的刺’呢？', null, '2017-06-16', null, 'A');
INSERT INTO `scripture` VALUES ('A168', '【太7:5】你这假冒为善的人！先去掉自己眼中的梁木，然后才能看得清楚，去掉你弟兄眼中的刺。', null, '2017-06-17', null, 'A');
INSERT INTO `scripture` VALUES ('A169', '【太7:6】不要把圣物给狗，也不要把你们的珍珠丢在猪前，恐怕它践踏了珍珠，转过来咬你们。', null, '2017-06-18', null, 'A');
INSERT INTO `scripture` VALUES ('A17', '【提后1:12】为这缘故，我也受这些苦难，然而我不以为耻。因为知道我所信的是谁，也深信他能保全我所交付他的（或作“他所交托我的”），直到那日。', null, '2017-01-17', null, 'A');
INSERT INTO `scripture` VALUES ('A170', '【太7:7-8】7“你们祈求，就给你们；寻找，就寻见；叩门，就给你们开门。8因为凡祈求的，就得着；寻找的，就寻见；叩门的，就给他开门。', null, '2017-06-19', null, 'A');
INSERT INTO `scripture` VALUES ('A171', '【太7:9-11】9你们中间谁有儿子求饼，反给他石头呢？10求鱼，反给他蛇呢？11你们虽然不好，尚且知道拿好东西给儿女，何况你们在天上的父，岂不更把好东西给求他的人吗？', null, '2017-06-20', null, 'A');
INSERT INTO `scripture` VALUES ('A172', '【太7:12】所以，无论何事，你们愿意人怎样待你们，你们也要怎样待人，因为这就是律法和先知的道理。”', null, '2017-06-21', null, 'A');
INSERT INTO `scripture` VALUES ('A173', '【太7:13-14】13“你们要进窄门。因为引到灭亡，那门是宽的，路是大的，进去的人也多；14引到永生，那门是窄的，路是小的，找着的人也少。”', null, '2017-06-22', null, 'A');
INSERT INTO `scripture` VALUES ('A174', '【太7:15-16】15“你们要防备假先知，他们到你们这里来，外面披着羊皮，里面却是残暴的狼。16凭着他们的果子，就可以认出他们来。荆棘上岂能摘葡萄呢？蒺藜里岂能摘无花果呢？', null, '2017-06-23', null, 'A');
INSERT INTO `scripture` VALUES ('A175', '【太7:21】“凡称呼我‘主啊，主啊’的人，不能都进天国；惟独遵行我天父旨意的人，才能进去。', null, '2017-06-24', null, 'A');
INSERT INTO `scripture` VALUES ('A176', '【太7:24-25】24“所以，凡听见我这话就去行的，好比一个聪明人，把房子盖在磐石上。25雨淋，水冲，风吹，撞着那房子，房子总不倒塌，因为根基立在磐石上。', null, '2017-06-25', null, 'A');
INSERT INTO `scripture` VALUES ('A177', '【太8:20】耶稣说：“狐狸有洞，天空的飞鸟有窝，人子却没有枕头的地方。”', null, '2017-06-26', null, 'A');
INSERT INTO `scripture` VALUES ('A178', '【太9:37-38】37于是对门徒说：“要收的庄稼多，作工的人少。38所以你们当求庄稼的主，打发工人出去收他的庄稼。”', null, '2017-06-27', null, 'A');
INSERT INTO `scripture` VALUES ('A179', '【太10:16】“我差你们去，如同羊进入狼群，所以你们要灵巧像蛇，驯良像鸽子。', null, '2017-06-28', null, 'A');
INSERT INTO `scripture` VALUES ('A18', '【提后2:3】你要和我同受苦难，好像基督耶稣的精兵。', null, '2017-01-18', null, 'A');
INSERT INTO `scripture` VALUES ('A180', '【太10:22】并且你们要为我的名被众人恨恶，惟有忍耐到底的必然得救。', null, '2017-06-29', null, 'A');
INSERT INTO `scripture` VALUES ('A181', '【太10:29-31】29两个麻雀不是卖一分银子吗？若是你们的父不许，一个也不能掉在地上。30就是你们的头发也都被数过了。31所以，不要惧怕，你们比许多麻雀还贵重。”', null, '2017-06-30', null, 'A');
INSERT INTO `scripture` VALUES ('A182', '【太10:32-33】32“凡在人面前认我的，我在我天上的父面前也必认他；33凡在人面前不认我的，我在我天上的父面前也必不认他。”', null, '2017-07-01', null, 'A');
INSERT INTO `scripture` VALUES ('A183', '【太10:37-39】37爱父母过于爱我的，不配作我的门徒；爱儿女过于爱我的，不配作我的门徒。38不背着他的十字架跟从我的，也不配作我的门徒。39得着生命的，将要失丧生命；为我失丧生命的，将要得着生命。”', null, '2017-07-02', null, 'A');
INSERT INTO `scripture` VALUES ('A184', '【太10:42】无论何人，因为门徒的名，只把一杯凉水给这小子里的一个喝，我实在告诉你们，这人不能不得赏赐。”', null, '2017-07-03', null, 'A');
INSERT INTO `scripture` VALUES ('A185', '【太11:12】从施洗约翰的时候到如今，天国是努力进入的，努力的人就得着了。', null, '2017-07-04', null, 'A');
INSERT INTO `scripture` VALUES ('A186', '【太11:28】凡劳苦担重担的人，可以到我这里来，我就使你们得安息。', null, '2017-07-05', null, 'A');
INSERT INTO `scripture` VALUES ('A187', '【太11:29-30】29我心里柔和谦卑，你们当负我的轭，学我的样式，这样，你们心里就必得享安息。30因为我的轭是容易的，我的担子是轻省的。”', null, '2017-07-06', null, 'A');
INSERT INTO `scripture` VALUES ('A188', '【太12:20】压伤的芦苇，他不折断；将残的灯火，他不吹灭。等他施行公理，叫公理得胜，', null, '2017-07-07', null, 'A');
INSERT INTO `scripture` VALUES ('A189', '【太12:36-37】36我又告诉你们：凡人所说的闲话，当审判的日子，必要句句供出来。37因为要凭你的话定你为义；也要凭你的话定你有罪。”', null, '2017-07-08', null, 'A');
INSERT INTO `scripture` VALUES ('A19', '【提后2:13】我们纵然失信，他仍是可信的，因为他不能背乎自己。”', null, '2017-01-19', null, 'A');
INSERT INTO `scripture` VALUES ('A190', '【太12:50】凡遵行我天父旨意的人，就是我的弟兄、姐妹和母亲了。', null, '2017-07-09', null, 'A');
INSERT INTO `scripture` VALUES ('A191', '【太16:15-16】15耶稣说：“你们说我是谁？”16西门彼得回答说：“你是基督，是永生神的儿子。”', null, '2017-07-10', null, 'A');
INSERT INTO `scripture` VALUES ('A192', '【太16:18】我还告诉你：你是彼得，我要把我的教会建造在这磐石上，阴间的权柄不能胜过他（“权柄”原文作“门”）。', null, '2017-07-11', null, 'A');
INSERT INTO `scripture` VALUES ('A193', '【太16:23】耶稣转过来，对彼得说：“撒但，退我后边去吧！你是绊我脚的，因为你不体贴神的意思，只体贴人的意思。”', null, '2017-07-12', null, 'A');
INSERT INTO `scripture` VALUES ('A194', '【太16:26】人若赚得全世界，赔上自己的生命，有什么益处呢？人还能拿什么换生命呢？', null, '2017-07-13', null, 'A');
INSERT INTO `scripture` VALUES ('A195', '【太17:20】耶稣说：“是因你们的信心小。我实在告诉你们，你们若有信心像一粒芥菜种，就是对这座山说，‘你从这边挪到那边’，它也必挪去，并且你们没有一件不能作的事了。', null, '2017-07-14', null, 'A');
INSERT INTO `scripture` VALUES ('A196', '【太18:3-4】3说：“我实在告诉你们：你们若不回转，变成小孩子的样式，断不得进天国。4所以，凡自己谦卑像这小孩子的，他在天国里就是最大的。', null, '2017-07-15', null, 'A');
INSERT INTO `scripture` VALUES ('A197', '【太18:19-20】19我又告诉你们：若是你们中间有两个人在地上同心合意地求什么事，我在天上的父必为他们成全。20因为无论在哪里，有两三个人奉我的名聚会，那里就有我在他们中间。', null, '2017-07-16', null, 'A');
INSERT INTO `scripture` VALUES ('A198', '【太18:21-22】21那时彼得进前来，对耶稣说：“主啊，我弟兄得罪我，我当饶恕他几次呢？到七次可以吗？”22耶稣说：“我对你说：不是到七次，乃是到七十个七次。', null, '2017-07-17', null, 'A');
INSERT INTO `scripture` VALUES ('A199', '【太19:6】既然如此，夫妻不再是两个人，乃是一体的了。所以，神配合的，人不可分开。”', null, '2017-07-18', null, 'A');
INSERT INTO `scripture` VALUES ('A2', '【帖前4:3-4】3神的旨意就是要你们成为圣洁，远避淫行；4要你们各人晓得怎样用圣洁、尊贵守着自己的身体，', null, '2017-01-02', null, 'A');
INSERT INTO `scripture` VALUES ('A20', '【提后2:15】你当竭力在神面前得蒙喜悦，作无愧的工人，按着正意分解真理的道。', null, '2017-01-20', null, 'A');
INSERT INTO `scripture` VALUES ('A200', '【太19:14】耶稣说：“让小孩子到我这里来，不要禁止他们，因为在天国的，正是这样的人。”', null, '2017-07-19', null, 'A');
INSERT INTO `scripture` VALUES ('A201', '【太19:23-24】23耶稣对门徒说：“我实在告诉你们：财主进天国是难的。24我又告诉你们：骆驼穿过针的眼，比财主进神的国还容易呢！', null, '2017-07-20', null, 'A');
INSERT INTO `scripture` VALUES ('A202', '【太19:26】耶稣看着他们，说：“在人这是不能的，在神凡事都能。”', null, '2017-07-21', null, 'A');
INSERT INTO `scripture` VALUES ('A203', '【太20:16】这样，那在后的将要在前；在前的将要在后了（有古卷在此有“因为被召的人多，选上的人少”）。”', null, '2017-07-22', null, 'A');
INSERT INTO `scripture` VALUES ('A204', '【太20:26-28】26只是在你们中间不可这样。你们中间谁愿为大，就必作你们的用人；27谁愿为首，就必作你们的仆人。28正如人子来，不是要受人的服侍，乃是要服侍人，并且要舍命，作多人的赎价。”', null, '2017-07-23', null, 'A');
INSERT INTO `scripture` VALUES ('A205', '【太22:21】他们说：“是凯撒的。”耶稣说：“这样，凯撒的物当归给凯撒；神的物当归给神。”', null, '2017-07-24', null, 'A');
INSERT INTO `scripture` VALUES ('A206', '【太22:37-40】37耶稣对他说：“你要尽心、尽性、尽意，爱主你的神。38这是诫命中的第一，且是最大的。39其次也相仿，就是要爱人如己。40这两条诫命是律法和先知一切道理的总纲。”', null, '2017-07-25', null, 'A');
INSERT INTO `scripture` VALUES ('A207', '【太23:12】凡自高的，必降为卑；自卑的，必升为高。', null, '2017-07-26', null, 'A');
INSERT INTO `scripture` VALUES ('A208', '【太23:37】“耶路撒冷啊，耶路撒冷啊！你常杀害先知，又用石头打死那奉差遣到你这里来的人。我多次愿意聚集你的儿女，好像母鸡把小鸡聚集在翅膀底下，只是你们不愿意。', null, '2017-07-27', null, 'A');
INSERT INTO `scripture` VALUES ('A209', '【太24:4-5】4耶稣回答说：“你们要谨慎，免得有人迷惑你们。5因为将来有好些人冒我的名来，说：‘我是基督’，并且要迷惑许多人。', null, '2017-07-28', null, 'A');
INSERT INTO `scripture` VALUES ('A21', '【提后2:21】人若自洁，脱离卑贱的事，就必作贵重的器皿，成为圣洁，合乎主用，预备行各样的善事。', null, '2017-01-21', null, 'A');
INSERT INTO `scripture` VALUES ('A210', '【太24:7-8】7民要攻打民，国要攻打国，多处必有饥荒、地震。8这都是灾难的起头（“灾难”原文作“生产之难”）。', null, '2017-07-29', null, 'A');
INSERT INTO `scripture` VALUES ('A211', '【太24:12-13】12只因不法的事增多，许多人的爱心才渐渐冷淡了。13惟有忍耐到底的必然得救。', null, '2017-07-30', null, 'A');
INSERT INTO `scripture` VALUES ('A212', '【太24:14】这天国的福音要传遍天下，对万民作见证，然后末期才来到。”', null, '2017-07-31', null, 'A');
INSERT INTO `scripture` VALUES ('A213', '【太24:32-33】32“你们可以从无花果树学个比方：当树枝发嫩长叶的时候，你们就知道夏天近了。33这样，你们看见这一切的事，也该知道人子近了，正在门口了。', null, '2017-08-01', null, 'A');
INSERT INTO `scripture` VALUES ('A214', '【太24:34-35】34我实在告诉你们：这世代还没有过去，这些事都要成就。35天地要废去，我的话却不能废去。”', null, '2017-08-02', null, 'A');
INSERT INTO `scripture` VALUES ('A215', '【太24:44】所以，你们也要预备，因为你们想不到的时候，人子就来了。”', null, '2017-08-03', null, 'A');
INSERT INTO `scripture` VALUES ('A216', '【太24:45】“谁是忠心有见识的仆人，为主人所派，管理家里的人，按时分粮给他们呢？', null, '2017-08-04', null, 'A');
INSERT INTO `scripture` VALUES ('A217', '【太25:3-4】3愚拙的拿着灯，却不预备油；4聪明的拿着灯，又预备油在器皿里。', null, '2017-08-05', null, 'A');
INSERT INTO `scripture` VALUES ('A218', '【太25:21】主人说：‘好，你这又良善又忠心的仆人，你在不多的事上有忠心，我要把许多事派你管理，可以进来享受你主人的快乐。’', null, '2017-08-06', null, 'A');
INSERT INTO `scripture` VALUES ('A219', '【太25:29-30】29因为凡有的，还要加给他，叫他有余；没有的，连他所有的也要夺过来。30把这无用的仆人丢在外面黑暗里，在那里必要哀哭切齿了。‘“', null, '2017-08-07', null, 'A');
INSERT INTO `scripture` VALUES ('A22', '【提后3:5】有敬虔的外貌，却背了敬虔的实意，这等人你要躲开。', null, '2017-01-22', null, 'A');
INSERT INTO `scripture` VALUES ('A220', '【太25:40】王要回答说：‘我实在告诉你们：这些事你们既作在我这弟兄中一个最小的身上，就是作在我身上了。’', null, '2017-08-08', null, 'A');
INSERT INTO `scripture` VALUES ('A221', '【太26:39】他就稍往前走，俯伏在地祷告说：“我父啊，倘若可行，求你叫这杯离开我；然而，不要照我的意思，只要照你的意思。”', null, '2017-08-09', null, 'A');
INSERT INTO `scripture` VALUES ('A222', '【太26:41】总要警醒祷告，免得入了迷惑，你们心灵固然愿意，肉体却软弱了。”', null, '2017-08-10', null, 'A');
INSERT INTO `scripture` VALUES ('A223', '【太26:52-53】52耶稣对他说：“收刀入鞘吧！凡动刀的，必死在刀下。53你想我不能求我父现在为我差遣十二营多天使来吗？', null, '2017-08-11', null, 'A');
INSERT INTO `scripture` VALUES ('A224', '【太26:75】彼得想起耶稣所说的话：“鸡叫以先，你要三次不认我。”他就出去痛哭。', null, '2017-08-12', null, 'A');
INSERT INTO `scripture` VALUES ('A225', '【太28:18】耶稣进前来，对他们说：“天上地下所有的权柄都赐给我了。', null, '2017-08-13', null, 'A');
INSERT INTO `scripture` VALUES ('A226', '【太28:19-20】19所以，你们要去，使万民作我的门徒，奉父、子、圣灵的名给他们施洗（或作“给他们施洗，归于父、子、圣灵的名”）。20凡我所吩咐你们的，都教训他们遵守，我就常与你们同在，直到世界的末了。”', null, '2017-08-14', null, 'A');
INSERT INTO `scripture` VALUES ('A227', '【可2:17】耶稣听见，就对他们说：“康健的人用不着医生，有病的人才用得着。我来本不是召义人，乃是召罪人。”', null, '2017-08-15', null, 'A');
INSERT INTO `scripture` VALUES ('A228', '【可9:23-24】23耶稣对他说：“你若能信，在信的人，凡事都能。”24孩子的父亲立时喊着说（有古卷作“立时流泪地喊着说”）：“我信！但我信不足，求主帮助！”', null, '2017-08-16', null, 'A');
INSERT INTO `scripture` VALUES ('A229', '【可11:24】所以我告诉你们：凡你们祷告祈求的，无论是什么，只要信是得着的，就必得着。', null, '2017-08-17', null, 'A');
INSERT INTO `scripture` VALUES ('A23', '【提后3:15】并且知道你是从小明白圣经，这圣经能使你因信基督耶稣有得救的智慧。', null, '2017-01-23', null, 'A');
INSERT INTO `scripture` VALUES ('A230', '【可12:43-44】43耶稣叫门徒来，说：“我实在告诉你们：这穷寡妇投入库里的，比众人所投的更多。44因为他们都是自己有余，拿出来投在里头；但这寡妇是自己不足，把她一切养生的都投上了。”', null, '2017-08-18', null, 'A');
INSERT INTO `scripture` VALUES ('A231', '【路1:37】因为出于神的话，没有一句不带能力的。”', null, '2017-08-19', null, 'A');
INSERT INTO `scripture` VALUES ('A232', '【路1:46-47】46马利亚说：“我心尊主为大，47我灵以神我的救主为乐;', null, '2017-08-20', null, 'A');
INSERT INTO `scripture` VALUES ('A233', '【路2:10】那天使对他们说：“不要惧怕，我报给你们大喜的信息，是关乎万民的。', null, '2017-08-21', null, 'A');
INSERT INTO `scripture` VALUES ('A234', '【路2:14】“在至高之处荣耀归与神，在地上平安归与他所喜悦的人（有古卷作“喜悦归与人”）。”', null, '2017-08-22', null, 'A');
INSERT INTO `scripture` VALUES ('A235', '【路2:19】马利亚却把这一切的事存在心里，反复思想。', null, '2017-08-23', null, 'A');
INSERT INTO `scripture` VALUES ('A236', '【路2:52】耶稣的智慧和身量（“身量”或作“年纪”），并神和人喜爱他的心，都一齐增长。', null, '2017-08-24', null, 'A');
INSERT INTO `scripture` VALUES ('A237', '【路6:38】你们要给人，就必有给你们的，并且用十足的升斗，连摇带按、上尖下流地倒在你们怀里；因为你们用什么量器量给人，也必用什么量器量给你们。”', null, '2017-08-25', null, 'A');
INSERT INTO `scripture` VALUES ('A238', '【路9:62】耶稣说：“手扶着犁向后看的，不配进神的国。”', null, '2017-08-26', null, 'A');
INSERT INTO `scripture` VALUES ('A239', '【路10:41-42】41耶稣回答说：“马大，马大！你为许多的事思虑烦扰，42但是不可少的只有一件，马利亚已经选择那上好的福分，是不能夺去的。”', null, '2017-08-27', null, 'A');
INSERT INTO `scripture` VALUES ('A24', '【提后3:16-17】16圣经都是神所默示的（或作“凡神所默示的圣经”），于教训、督责、使人归正、教导人学义都是有益的，17叫属神的人得以完全，预备行各样的善事。', null, '2017-01-24', null, 'A');
INSERT INTO `scripture` VALUES ('A240', '【路12:15】于是对众人说：“你们要谨慎自守，免去一切的贪心；因为人的生命不在乎家道丰富。”', null, '2017-08-28', null, 'A');
INSERT INTO `scripture` VALUES ('A241', '【路12:48】惟有那不知道的，做了当受责打的事，必少受责打。因为多给谁，就向谁多取；多托谁，就向谁多要。', null, '2017-08-29', null, 'A');
INSERT INTO `scripture` VALUES ('A242', '【路15:7】我告诉你们：一个罪人悔改，在天上也要这样为他欢喜，较比为九十九个不用悔改的义人欢喜更大。”', null, '2017-08-30', null, 'A');
INSERT INTO `scripture` VALUES ('A243', '【路16:10】人在最小的事上忠心，在大事上也忠心；在最小的事上不义，在大事上也不义。', null, '2017-08-31', null, 'A');
INSERT INTO `scripture` VALUES ('A244', '【路19:10】人子来，为要寻找、拯救失丧的人。', null, '2017-09-01', null, 'A');
INSERT INTO `scripture` VALUES ('A245', '【路22:44】耶稣极其伤痛，祷告更加恳切，汗珠如大血点，滴在地上。', null, '2017-09-02', null, 'A');
INSERT INTO `scripture` VALUES ('A246', '【路23:34】当下耶稣说：“父啊，赦免他们！因为他们所作的，他们不晓得。”兵丁就拈阄分他的衣服。', null, '2017-09-03', null, 'A');
INSERT INTO `scripture` VALUES ('A247', '【路23:38】在耶稣以上有一个牌子（有古卷在此有“用希腊、罗马、希伯来的文字”）写着：“这是犹太人的王。”', null, '2017-09-04', null, 'A');
INSERT INTO `scripture` VALUES ('A248', '【路24:6-7】6他不在这里，已经复活了。当记念他还在加利利的时候怎样告诉你们，7说：‘人子必须被交在罪人手里，钉在十字架上，第三日复活。’”', null, '2017-09-05', null, 'A');
INSERT INTO `scripture` VALUES ('A249', '【约1:1-2】1太初有道，道与神同在，道就是神。2这道太初与神同在。', null, '2017-09-06', null, 'A');
INSERT INTO `scripture` VALUES ('A25', '【提后4:2】务要传道，无论得时不得时，总要专心，并用百般的忍耐，各样的教训，责备人，警戒人，劝勉人。', null, '2017-01-25', null, 'A');
INSERT INTO `scripture` VALUES ('A250', '【约1:3】万物是藉着他造的；凡被造的，没有一样不是藉着他造的。', null, '2017-09-07', null, 'A');
INSERT INTO `scripture` VALUES ('A251', '【约1:4】生命在他里头，这生命就是人的光。', null, '2017-09-08', null, 'A');
INSERT INTO `scripture` VALUES ('A252', '【约1:12-13】12凡接待他的，就是信他名的人，他就赐他们权柄，作神的儿女。13这等人不是从血气生的，不是从情欲生的，也不是从人意生的，乃是从神生的。', null, '2017-09-09', null, 'A');
INSERT INTO `scripture` VALUES ('A253', '【约1:14】道成了肉身，住在我们中间，充充满满地有恩典，有真理。我们也见过他的荣光，正是父独生子的荣光。', null, '2017-09-10', null, 'A');
INSERT INTO `scripture` VALUES ('A254', '【约1:18】从来没有人看见神，只有在父怀里的独生子将他表明出来。', null, '2017-09-11', null, 'A');
INSERT INTO `scripture` VALUES ('A255', '【约1:29】次日，约翰看见耶稣来到他那里，就说：“看哪，神的羔羊，除去（或作“背负”）世人罪孽的。', null, '2017-09-12', null, 'A');
INSERT INTO `scripture` VALUES ('A256', '【约1:46-47】46拿但业对他说：“拿撒勒还能出什么好的吗？”腓力说：“你来看。”47耶稣看见拿但业来，就指着他说：“看哪，这是个真以色列人，他心里是没有诡诈的。”', null, '2017-09-13', null, 'A');
INSERT INTO `scripture` VALUES ('A257', '【约3:5】耶稣说：“我实实在在地告诉你：人若不是从水和圣灵生的，就不能进神的国。', null, '2017-09-14', null, 'A');
INSERT INTO `scripture` VALUES ('A258', '【约3:16】“ 神爱世人，甚至将他的独生子赐给他们，叫一切信他的，不至灭亡，反得永生。', null, '2017-09-15', null, 'A');
INSERT INTO `scripture` VALUES ('A259', '【约3:30】他必兴旺，我必衰微。”', null, '2017-09-16', null, 'A');
INSERT INTO `scripture` VALUES ('A26', '【提后4:7-8】7那美好的仗我已经打过了，当跑的路我已经跑尽了，所信的道我已经守住了。8从此以后，有公义的冠冕为我存留，就是按着公义审判的主到了那日要赐给我的，不但赐给我，也赐给凡爱慕他显现的人。', null, '2017-01-26', null, 'A');
INSERT INTO `scripture` VALUES ('A260', '【约3:36】信子的人有永生；不信子的人得不着永生（原文作“不得见永生”），神的震怒常在他身上。”', null, '2017-09-17', null, 'A');
INSERT INTO `scripture` VALUES ('A261', '【约4:13-14】13耶稣回答说：“凡喝这水的，还要再渴；14人若喝我所赐的水，就永远不渴。我所赐的水要在他里头成为泉源，直涌到永生。”', null, '2017-09-18', null, 'A');
INSERT INTO `scripture` VALUES ('A262', '【约4:24】神是个灵（或无“个”字），所以拜他的，必须用心灵和诚实拜他。”', null, '2017-09-19', null, 'A');
INSERT INTO `scripture` VALUES ('A263', '【约5:24】我实实在在地告诉你们：那听我话、又信差我来者的，就有永生，不至于定罪，是已经出死入生了。', null, '2017-09-20', null, 'A');
INSERT INTO `scripture` VALUES ('A264', '【约5:39】你们查考圣经（或作“应当查考圣经”），因你们以为内中有永生，给我作见证的就是这经。', null, '2017-09-21', null, 'A');
INSERT INTO `scripture` VALUES ('A265', '【约6:26-27】26耶稣回答说：“我实实在在地告诉你们：你们找我，并不是因见了神迹，乃是因吃饼得饱。27不要为那必坏的食物劳力，要为那存到永生的食物劳力，就是人子要赐给你们的，因为人子是父神所印证的。”', null, '2017-09-22', null, 'A');
INSERT INTO `scripture` VALUES ('A266', '【约6:28-29】28众人问他说：“我们当行什么，才算作神的工呢？”29耶稣回答说：“信神所差来的，这就是作神的工。”', null, '2017-09-23', null, 'A');
INSERT INTO `scripture` VALUES ('A267', '【约6:35】耶稣说：“我就是生命的粮，到我这里来的，必定不饿；信我的，永远不渴。', null, '2017-09-24', null, 'A');
INSERT INTO `scripture` VALUES ('A268', '【约6:63】叫人活着的乃是灵，肉体是无益的。我对你们所说的话就是灵，就是生命。', null, '2017-09-25', null, 'A');
INSERT INTO `scripture` VALUES ('A269', '【约7:38】信我的人，就如经上所说‘从他腹中要流出活水的江河来’。”', null, '2017-09-26', null, 'A');
INSERT INTO `scripture` VALUES ('A27', '【来1:1-2】1神既在古时藉着众先知多次多方地晓谕列祖;2就在这末世藉着他儿子晓谕我们，又早已立他为承受万有的;也曾藉着他创造诸世界。', null, '2017-01-27', null, 'A');
INSERT INTO `scripture` VALUES ('A270', '【约8:12】耶稣又对众人说：“我是世界的光。跟从我的，就不在黑暗里走，必要得着生命的光。”', null, '2017-09-27', null, 'A');
INSERT INTO `scripture` VALUES ('A271', '【约8:32】你们必晓得真理，真理必叫你们得以自由。”', null, '2017-09-28', null, 'A');
INSERT INTO `scripture` VALUES ('A272', '【约10:10】盗贼来，无非要偷窃、杀害、毁坏；我来了，是要叫羊（或作“人”）得生命，并且得的更丰盛。', null, '2017-09-29', null, 'A');
INSERT INTO `scripture` VALUES ('A273', '【约10:11】我是好牧人，好牧人为羊舍命。', null, '2017-09-30', null, 'A');
INSERT INTO `scripture` VALUES ('A274', '【约10:27-28】27我的羊听我的声音，我也认识他们，他们也跟着我。28我又赐给他们永生，他们永不灭亡，谁也不能从我手里把他们夺去。', null, '2017-10-01', null, 'A');
INSERT INTO `scripture` VALUES ('A275', '【约11:25】耶稣对她说：“复活在我，生命也在我；信我的人，虽然死了，也必复活。', null, '2017-10-02', null, 'A');
INSERT INTO `scripture` VALUES ('A276', '【约12:24】我实实在在地告诉你们：一粒麦子不落在地里死了，仍旧是一粒；若是死了，就结出许多子粒来。', null, '2017-10-03', null, 'A');
INSERT INTO `scripture` VALUES ('A277', '【约12:26】若有人服侍我，就当跟从我；我在哪里，服侍我的人也要在那里；若有人服侍我，我父必尊重他。”', null, '2017-10-04', null, 'A');
INSERT INTO `scripture` VALUES ('A278', '【约12:32】我若从地上被举起来，就要吸引万人来归我。”', null, '2017-10-05', null, 'A');
INSERT INTO `scripture` VALUES ('A279', '【约13:34-35】34我赐给你们一条新命令，乃是叫你们彼此相爱；我怎样爱你们，你们也要怎样相爱。35你们若有彼此相爱的心，众人因此就认出你们是我的门徒了。”', null, '2017-10-06', null, 'A');
INSERT INTO `scripture` VALUES ('A28', '【来1:3】他是神荣耀所发的光辉，是神本体的真像，常用他权能的命令托住万有。他洗净了人的罪，就坐在高天至大者的右边。', null, '2017-01-28', null, 'A');
INSERT INTO `scripture` VALUES ('A280', '【约14:6】耶稣说：“我就是道路、真理、生命；若不藉着我，没有人能到父那里去。', null, '2017-10-07', null, 'A');
INSERT INTO `scripture` VALUES ('A281', '【约14:16】我要求父，父就另外赐给你们一位保惠师（或作“训慰师”。下同），叫他永远与你们同在，', null, '2017-10-08', null, 'A');
INSERT INTO `scripture` VALUES ('A282', '【约14:21】有了我的命令又遵守的，这人就是爱我的；爱我的必蒙我父爱他，我也要爱他，并且要向他显现。”', null, '2017-10-09', null, 'A');
INSERT INTO `scripture` VALUES ('A283', '【约14:26】但保惠师，就是父因我的名所要差来的圣灵，他要将一切的事指教你们，并且要叫你们想起我对你们所说的一切话。', null, '2017-10-10', null, 'A');
INSERT INTO `scripture` VALUES ('A284', '【约15:1】“我是真葡萄树，我父是栽培的人。', null, '2017-10-11', null, 'A');
INSERT INTO `scripture` VALUES ('A285', '【约15:5】我是葡萄树，你们是枝子；常在我里面的，我也常在他里面，这人就多结果子；因为离了我，你们就不能作什么。', null, '2017-10-12', null, 'A');
INSERT INTO `scripture` VALUES ('A286', '【约15:16】不是你们拣选了我，是我拣选了你们；并且分派你们去结果子，叫你们的果子常存，使你们奉我的名，无论向父求什么，他就赐给你们。', null, '2017-10-13', null, 'A');
INSERT INTO `scripture` VALUES ('A287', '【约16:8】他既来了，就要叫世人为罪、为义、为审判，自己责备自己。', null, '2017-10-14', null, 'A');
INSERT INTO `scripture` VALUES ('A288', '【约16:13】只等真理的圣灵来了，他要引导你们明白（原文作“进入”）一切的真理；因为他不是凭自己说的，乃是把他所听见的都说出来，并要把将来的事告诉你们。', null, '2017-10-15', null, 'A');
INSERT INTO `scripture` VALUES ('A289', '【约16:33】我将这些事告诉你们，是要叫你们在我里面有平安。在世上你们有苦难，但你们可以放心，我已经胜了世界。”', null, '2017-10-16', null, 'A');
INSERT INTO `scripture` VALUES ('A29', '【来1:14】天使岂不都是服役的灵、奉差遣为那将要承受救恩的人效力吗？', null, '2017-01-29', null, 'A');
INSERT INTO `scripture` VALUES ('A290', '【约17:3】认识你独一的真神，并且认识你所差来的耶稣基督，这就是永生。', null, '2017-10-17', null, 'A');
INSERT INTO `scripture` VALUES ('A291', '【约17:15】我不求你叫他们离开世界，只求你保守他们脱离那恶者（或作“脱离罪恶”）。', null, '2017-10-18', null, 'A');
INSERT INTO `scripture` VALUES ('A292', '【约17:17】求你用真理使他们成圣，你的道就是真理。', null, '2017-10-19', null, 'A');
INSERT INTO `scripture` VALUES ('A293', '【约19:26-27】26耶稣见母亲和他所爱的那门徒站在旁边，就对他母亲说：“母亲（原文作“妇人”），看，你的儿子！”27又对那门徒说：“看，你的母亲！”从此那门徒就接她到自己家里去了。', null, '2017-10-20', null, 'A');
INSERT INTO `scripture` VALUES ('A294', '【约20:19】那日（就是七日的第一日）晚上，门徒所在的地方，因怕犹太人，门都关了。耶稣来站在当中，对他们说：“愿你们平安！”', null, '2017-10-21', null, 'A');
INSERT INTO `scripture` VALUES ('A295', '【约20:29】耶稣对他说：“你因看见了我才信；那没有看见就信的有福了。”', null, '2017-10-22', null, 'A');
INSERT INTO `scripture` VALUES ('A296', '【约20:31】但记这些事，要叫你们信耶稣是基督，是神的儿子，并且叫你们信了他，就可以因他的名得生命。', null, '2017-10-23', null, 'A');
INSERT INTO `scripture` VALUES ('A297', '【徒1:8】但圣灵降临在你们身上，你们就必得着能力；并要在耶路撒冷、犹太全地和撒玛利亚，直到地极，作我的见证。”', null, '2017-10-24', null, 'A');
INSERT INTO `scripture` VALUES ('A298', '【徒4:12】除他以外，别无拯救。因为在天下人间，没有赐下别的名，我们可以靠着得救。”', null, '2017-10-25', null, 'A');
INSERT INTO `scripture` VALUES ('A299', '【徒7:22】摩西学了埃及人一切的学问，说话行事都有才能。', null, '2017-10-26', null, 'A');
INSERT INTO `scripture` VALUES ('A3', '【帖前4:15-16】15我们现在照主的话告诉你们一件事：我们这活着还存留到主降临的人，断不能在那已经睡了的人之先，16因为主必亲自从天降临，有呼叫的声音和天使长的声音，又有神的号吹响，那在基督里死了的人必先复活。', null, '2017-01-03', null, 'A');
INSERT INTO `scripture` VALUES ('A30', '【来2:3】我们若忽略这么大的救恩，怎能逃罪呢？这救恩起先是主亲自讲的，后来是听见的人给我们证实了。', null, '2017-01-30', null, 'A');
INSERT INTO `scripture` VALUES ('A300', '【徒7:55-56】55但司提反被圣灵充满，定睛望天，看见神的荣耀，又看见耶稣站在神的右边，56就说：“我看见天开了，人子站在神的右边。”', null, '2017-10-27', null, 'A');
INSERT INTO `scripture` VALUES ('A301', '【徒16:31】他们说：“当信主耶稣，你和你一家都必得救。”', null, '2017-10-28', null, 'A');
INSERT INTO `scripture` VALUES ('A302', '【徒17:11】这地方的人贤于帖撒罗尼迦的人，甘心领受这道，天天考查圣经，要晓得这道是与不是。', null, '2017-10-29', null, 'A');
INSERT INTO `scripture` VALUES ('A303', '【徒17:28】我们生活、动作、存留，都在乎他。就如你们作诗的，有人说：‘我们也是他所生的。’', null, '2017-10-30', null, 'A');
INSERT INTO `scripture` VALUES ('A304', '【徒20:24】我却不以性命为念，也不看为宝贵，只要行完我的路程，成就我从主耶稣所领受的职事，证明神恩惠的福音。', null, '2017-10-31', null, 'A');
INSERT INTO `scripture` VALUES ('A305', '【徒20:28】圣灵立你们作全群的监督，你们就当为自己谨慎，也为全群谨慎，牧养神的教会，就是他用自己血所买来的（或作“救赎的”）。', null, '2017-11-01', null, 'A');
INSERT INTO `scripture` VALUES ('A306', '【徒20:35】我凡事给你们作榜样，叫你们知道应当这样劳苦，扶助软弱的人，又当记念主耶稣的话，说：‘施比受更为有福。’”', null, '2017-11-02', null, 'A');
INSERT INTO `scripture` VALUES ('A307', '【徒23:11】当夜，主站在保罗旁边，说：“放心吧！你怎样在耶路撒冷为我作见证，也必怎样在罗马为我作见证。”', null, '2017-11-03', null, 'A');
INSERT INTO `scripture` VALUES ('A308', '【徒26:18】我差你到他们那里去，要叫他们的眼睛得开，从黑暗中归向光明，从撒但权下归向神；又因信我，得蒙赦罪，和一切成圣的人同得基业。’”', null, '2017-11-04', null, 'A');
INSERT INTO `scripture` VALUES ('A309', '【徒27:25】所以众位可以放心，我信神他怎样对我说，事情也要怎样成就。', null, '2017-11-05', null, 'A');
INSERT INTO `scripture` VALUES ('A31', '【来2:14-15】14儿女既同有血肉之体，他也照样亲自成了血肉之体，特要藉着死，败坏那掌死权的，就是魔鬼，15并要释放那些一生因怕死而为奴仆的人。', null, '2017-01-31', null, 'A');
INSERT INTO `scripture` VALUES ('A310', '【罗1:16】我不以福音为耻；这福音本是神的大能，要救一切相信的，先是犹太人，后是希腊人。', null, '2017-11-06', null, 'A');
INSERT INTO `scripture` VALUES ('A311', '【罗1:20】自从造天地以来，神的永能和神性是明明可知的，虽是眼不能见，但藉着所造之物就可以晓得，叫人无可推诿。', null, '2017-11-07', null, 'A');
INSERT INTO `scripture` VALUES ('A312', '【罗3:10】就如经上所记：“没有义人，连一个也没有；', null, '2017-11-08', null, 'A');
INSERT INTO `scripture` VALUES ('A313', '【罗3:20】所以凡有血气的，没有一个因行律法能在神面前称义，因为律法本是叫人知罪。', null, '2017-11-09', null, 'A');
INSERT INTO `scripture` VALUES ('A314', '【罗3:23-24】23因为世人都犯了罪，亏缺了神的荣耀，24如今却蒙神的恩典，因基督耶稣的救赎，就白白地称义。', null, '2017-11-10', null, 'A');
INSERT INTO `scripture` VALUES ('A315', '【罗4:4-5】4作工的得工价，不算恩典，乃是该得的；5惟有不作工的，只信称罪人为义的神，他的信就算为义。', null, '2017-11-11', null, 'A');
INSERT INTO `scripture` VALUES ('A316', '【罗5:7-8】7为义人死，是少有的；为仁人死，或者有敢作的。8惟有基督在我们还作罪人的时候为我们死，神的爱就在此向我们显明了。', null, '2017-11-12', null, 'A');
INSERT INTO `scripture` VALUES ('A317', '【罗5:12】这就如罪是从一人入了世界，死又是从罪来的；于是死就临到众人，因为众人都犯了罪。', null, '2017-11-13', null, 'A');
INSERT INTO `scripture` VALUES ('A318', '【罗6:3-4】3岂不知我们这受洗归入基督耶稣的人，是受洗归入他的死吗？4所以我们藉着洗礼归入死，和他一同埋葬，原是叫我们一举一动有新生的样式，像基督藉着父的荣耀从死里复活一样。', null, '2017-11-14', null, 'A');
INSERT INTO `scripture` VALUES ('A319', '【罗6:23】因为罪的工价乃是死；惟有神的恩赐，在我们的主基督耶稣里，乃是永生。', null, '2017-11-15', null, 'A');
INSERT INTO `scripture` VALUES ('A32', '【来2:18】他自己既然被试探而受苦，就能搭救被试探的人。', null, '2017-02-01', null, 'A');
INSERT INTO `scripture` VALUES ('A320', '【罗7:18】我也知道在我里头，就是我肉体之中，没有良善。因为立志为善由得我，只是行出来由不得我。', null, '2017-11-16', null, 'A');
INSERT INTO `scripture` VALUES ('A321', '【罗8:2】因为赐生命圣灵的律在基督耶稣里释放了我，使我脱离罪和死的律了。', null, '2017-11-17', null, 'A');
INSERT INTO `scripture` VALUES ('A322', '【罗8:6】体贴肉体的就是死，体贴圣灵的乃是生命平安。', null, '2017-11-18', null, 'A');
INSERT INTO `scripture` VALUES ('A323', '【罗8:16-17】16圣灵与我们的心同证我们是神的儿女；17既是儿女，便是后嗣，就是神的后嗣，和基督同作后嗣。如果我们和他一同受苦，也必和他一同得荣耀。', null, '2017-11-19', null, 'A');
INSERT INTO `scripture` VALUES ('A324', '【罗8:18】我想，现在的苦楚若比起将来要显于我们的荣耀，就不足介意了。', null, '2017-11-20', null, 'A');
INSERT INTO `scripture` VALUES ('A325', '【罗8:26】况且，我们的软弱有圣灵帮助，我们本不晓得当怎样祷告，只是圣灵亲自用说不出来的叹息替我们祷告。', null, '2017-11-21', null, 'A');
INSERT INTO `scripture` VALUES ('A326', '【罗8:28】我们晓得万事都互相效力，叫爱神的人得益处，就是按他旨意被召的人。', null, '2017-11-22', null, 'A');
INSERT INTO `scripture` VALUES ('A327', '【罗8:32】神既不爱惜自己的儿子为我们众人舍了，岂不也把万物和他一同白白地赐给我们吗？', null, '2017-11-23', null, 'A');
INSERT INTO `scripture` VALUES ('A328', '【罗8:35】谁能使我们与基督的爱隔绝呢？难道是患难吗？是困苦吗？是逼迫吗？是饥饿吗？是赤身露体吗？是危险吗？是刀剑吗？', null, '2017-11-24', null, 'A');
INSERT INTO `scripture` VALUES ('A329', '【罗8:37】然而，靠着爱我们的主，在这一切的事上已经得胜有余了。', null, '2017-11-25', null, 'A');
INSERT INTO `scripture` VALUES ('A33', '【来4:12】神的道是活泼的，是有功效的，比一切两刃的剑更快，甚至魂与灵、骨节与骨髓，都能刺入、剖开，连心中的思念和主意都能辨明。', null, '2017-02-02', null, 'A');
INSERT INTO `scripture` VALUES ('A330', '【罗10:4】律法的总结就是基督，使凡信他的都得着义。', null, '2017-11-26', null, 'A');
INSERT INTO `scripture` VALUES ('A331', '【罗10:9-10】9你若口里认耶稣为主，心里信神叫他从死里复活，就必得救。10因为人心里相信，就可以称义；口里承认，就可以得救。', null, '2017-11-27', null, 'A');
INSERT INTO `scripture` VALUES ('A332', '【罗10:17】可见信道是从听道来的，听道是从基督的话来的。', null, '2017-11-28', null, 'A');
INSERT INTO `scripture` VALUES ('A333', '【罗12:1】所以弟兄们，我以神的慈悲劝你们，将身体献上，当作活祭，是圣洁的，是神所喜悦的，你们如此侍奉，乃是理所当然的。', null, '2017-11-29', null, 'A');
INSERT INTO `scripture` VALUES ('A334', '【罗12:2】不要效法这个世界，只要心意更新而变化，叫你们察验何为神的善良、纯全、可喜悦的旨意。', null, '2017-11-30', null, 'A');
INSERT INTO `scripture` VALUES ('A335', '【罗12:11】殷勤不可懒惰。要心里火热，常常服侍主。', null, '2017-12-01', null, 'A');
INSERT INTO `scripture` VALUES ('A336', '【罗12:19】亲爱的弟兄，不要自己伸冤，宁可让步，听凭主怒（或作“让人发怒”）。因为经上记着：“主说：伸冤在我，我必报应。”', null, '2017-12-02', null, 'A');
