INSERT INTO `scripture` VALUES ('A55', '【雅1:19-20】19我亲爱的弟兄们，这是你们所知道的。但你们各人要快快地听，慢慢地说，慢慢地动怒，20因为人的怒气并不成就神的义。', null, '2017-02-24', null, 'A');
