INSERT INTO `scripture` VALUES ('A579', null, null, '2018-07-31', null, 'A');
INSERT INTO `scripture` VALUES ('A58', '【雅2:13】因为那不怜悯人的，也要受无怜悯的审判，怜悯原是向审判夸胜。', null, '2017-02-27', null, 'A');
INSERT INTO `scripture` VALUES ('A580', null, null, '2018-07-31', null, 'A');
INSERT INTO `scripture` VALUES ('A581', null, null, '2018-07-31', null, 'A');
INSERT INTO `scripture` VALUES ('A59', '【雅2:17】这样，信心若没有行为就是死的。', null, '2017-02-28', null, 'A');
INSERT INTO `scripture` VALUES ('A6', '【帖前5:19-22】19不要消灭圣灵的感动，20不要藐视先知的讲论。21但要凡事察验，善美的要持守，22各样的恶事要禁戒不作。', null, '2017-01-06', null, 'A');
INSERT INTO `scripture` VALUES ('A60', '【雅2:19】你信神只有一位，你信的不错；鬼魔也信，却是战惊。', null, '2017-03-01', null, 'A');
INSERT INTO `scripture` VALUES ('A61', '【雅3:2】原来我们在许多事上都有过失；若有人在话语上没有过失，他就是完全人，也能勒住自己的全身。', null, '2017-03-02', null, 'A');
INSERT INTO `scripture` VALUES ('A62', '【雅3:5】这样，舌头在百体里也是最小的，却能说大话。看哪，最小的火能点着最大的树林。', null, '2017-03-03', null, 'A');
INSERT INTO `scripture` VALUES ('A63', '【雅4:3】你们求也得不着，是因为你们妄求，要浪费在你们的宴乐中。', null, '2017-03-04', null, 'A');
INSERT INTO `scripture` VALUES ('A64', '【雅4:4】你们这些淫乱的人哪（“淫乱的人”原文作“淫妇”），岂不知与世俗为友就是与神为敌吗？所以凡想要与世俗为友的，就是与神为敌了。', null, '2017-03-05', null, 'A');
INSERT INTO `scripture` VALUES ('A65', '【雅4:8】你们亲近神，神就必亲近你们。有罪的人哪，要洁净你们的手；心怀二意的人哪，要清洁你们的心。', null, '2017-03-06', null, 'A');
INSERT INTO `scripture` VALUES ('A66', '【雅4:14】其实明天如何，你们还不知道。你们的生命是什么呢？你们原来是一片云雾，出现少时就不见了。', null, '2017-03-07', null, 'A');
INSERT INTO `scripture` VALUES ('A67', '【雅5:7】弟兄们哪，你们要忍耐，直到主来。看哪，农夫忍耐等候地里宝贵的出产，直到得了秋雨春雨。', null, '2017-03-08', null, 'A');
INSERT INTO `scripture` VALUES ('A68', '【雅5:16】所以你们要彼此认罪，互相代求，使你们可以得医治。义人祈祷所发的力量是大有功效的。', null, '2017-03-09', null, 'A');
INSERT INTO `scripture` VALUES ('A69', '【彼前1:7】叫你们的信心既被试验，就比那被火试验仍然能坏的金子更显宝贵，可以在耶稣基督显现的时候，得着称赞、荣耀、尊贵。', null, '2017-03-10', null, 'A');
INSERT INTO `scripture` VALUES ('A7', '【帖后3:10】我们在你们那里的时候，曾吩咐你们说，若有人不肯作工，就不可吃饭。', null, '2017-01-07', null, 'A');
INSERT INTO `scripture` VALUES ('A70', '【彼前2:2】就要爱慕那纯净的灵奶，像才生的婴孩爱慕奶一样，叫你们因此渐长，以致得救。', null, '2017-03-11', null, 'A');
INSERT INTO `scripture` VALUES ('A71', '【彼前2:9】惟有你们是被拣选的族类，是有君尊的祭司，是圣洁的国度，是属神的子民，要叫你们宣扬那召你们出黑暗、入奇妙光明者的美德。', null, '2017-03-12', null, 'A');
INSERT INTO `scripture` VALUES ('A72', '【彼前2:13-14】13你们为主的缘故，要顺服人的一切制度，或是在上的君王，14或是君王所派、罚恶赏善的臣宰。', null, '2017-03-13', null, 'A');
INSERT INTO `scripture` VALUES ('A73', '【彼前2:24】他被挂在木头上，亲身担当了我们的罪，使我们既然在罪上死，就得以在义上活。因他受的鞭伤，你们便得了医治。', null, '2017-03-14', null, 'A');
INSERT INTO `scripture` VALUES ('A74', '【彼前3:15】只要心里尊主基督为圣。有人问你们心中盼望的缘由，就要常作准备，以温柔、敬畏的心回答各人。', null, '2017-03-15', null, 'A');
INSERT INTO `scripture` VALUES ('A75', '【彼前4:1】基督既在肉身受苦，你们也当将这样的心志作为兵器，因为在肉身受过苦的，就已经与罪断绝了。', null, '2017-03-16', null, 'A');
INSERT INTO `scripture` VALUES ('A76', '【彼前4:7-10】7万物的结局近了，所以你们要谨慎自守，警醒祷告。8最要紧的是彼此切实相爱，因为爱能遮掩许多的罪。9你们要互相款待，不发怨言。10各人要照所得的恩赐彼此服侍，作神百般恩赐的好管家。', null, '2017-03-17', null, 'A');
INSERT INTO `scripture` VALUES ('A77', '【彼前4:17】因为时候到了，审判要从神的家起首；若是先从我们起首，那不信从神福音的人将有何等的结局呢？', null, '2017-03-18', null, 'A');
INSERT INTO `scripture` VALUES ('A78', '【彼前5:2-3】2务要牧养在你们中间神的群羊，按着神旨意照管他们。不是出于勉强，乃是出于甘心；也不是因为贪财，乃是出于乐意；3也不是辖制所托付你们的，乃是作群羊的榜样。', null, '2017-03-19', null, 'A');
INSERT INTO `scripture` VALUES ('A79', '【彼前5:5】你们年幼的，也要顺服年长的。就是你们众人也都要以谦卑束腰，彼此顺服，因为神阻挡骄傲的人，赐恩给谦卑的人。', null, '2017-03-20', null, 'A');
INSERT INTO `scripture` VALUES ('A8', '【提前1:5】但命令的总归就是爱，这爱是从清洁的心和无亏的良心、无伪的信心生出来的。', null, '2017-01-08', null, 'A');
INSERT INTO `scripture` VALUES ('A80', '【彼前5:7】你们要将一切的忧虑卸给神，因为他顾念你们。', null, '2017-03-21', null, 'A');
INSERT INTO `scripture` VALUES ('A81', '【彼前5:8】务要谨守、警醒，因为你们的仇敌魔鬼，如同吼叫的狮子，遍地游行，寻找可吞吃的人。', null, '2017-03-22', null, 'A');
INSERT INTO `scripture` VALUES ('A82', '【彼后1:5-7】5正因这缘故，你们要分外地殷勤。有了信心，又要加上德行；有了德行，又要加上知识；6有了知识，又要加上节制；有了节制，又要加上忍耐；有了忍耐，又要加上虔敬；7有了虔敬，又要加上爱弟兄的心；有了爱弟兄的心，又要加上爱众人的心。', null, '2017-03-23', null, 'A');
INSERT INTO `scripture` VALUES ('A83', '【彼后1:20-21】20第一要紧的，该知道经上所有的预言没有可随私意解说的；21因为预言从来没有出于人意的，乃是人被圣灵感动，说出神的话来。', null, '2017-03-24', null, 'A');
INSERT INTO `scripture` VALUES ('A84', '【彼后3:8】亲爱的弟兄啊，有一件事你们不可忘记，就是主看一日如千年，千年如一日。', null, '2017-03-25', null, 'A');
INSERT INTO `scripture` VALUES ('A85', '【彼后3:9】主所应许的尚未成就，有人以为他是耽延，其实不是耽延，乃是宽容你们，不愿有一人沉沦，乃愿人人都悔改。', null, '2017-03-26', null, 'A');
INSERT INTO `scripture` VALUES ('A86', '【约一1:5】神就是光，在他毫无黑暗。这是我们从主所听见，又报给你们的信息。', null, '2017-03-27', null, 'A');
INSERT INTO `scripture` VALUES ('A87', '【约壹1:9】我们若认自己的罪，神是信实的，是公义的，必要赦免我们的罪，洗净我们一切的不义；', null, '2017-03-28', null, 'A');
INSERT INTO `scripture` VALUES ('A88', '【约壹2:15-16】15不要爱世界和世界上的事。人若爱世界，爱父的心就不在他里面了。16因为凡世界上的事，就像肉体的情欲，眼目的情欲，并今生的骄傲，都不是从父来的，乃是从世界来的。', null, '2017-03-29', null, 'A');
INSERT INTO `scripture` VALUES ('A89', '【约壹2:17】这世界和其上的情欲都要过去，惟独遵行神旨意的，是永远常存。', null, '2017-03-30', null, 'A');
INSERT INTO `scripture` VALUES ('A9', '【提前1:15】“基督耶稣降世，为要拯救罪人。”这话是可信的，是十分可佩服的。在罪人中我是个罪魁。', null, '2017-01-09', null, 'A');
INSERT INTO `scripture` VALUES ('A90', '【约一2:27】你们从主所受的恩膏常存在你们心里，并不用人教训你们，自有主的恩膏在凡事上教训你们。这恩膏是真的，不是假的，你们要按这恩膏的教训住在主里面。', null, '2017-03-31', null, 'A');
INSERT INTO `scripture` VALUES ('A91', '【约一3:18】小子们哪，我们相爱，不要只在言语和舌头上，总要在行为和诚实上。', null, '2017-04-01', null, 'A');
INSERT INTO `scripture` VALUES ('A92', '【约一4:7-8】7亲爱的弟兄啊，我们应当彼此相爱，因为爱是从神来的。凡有爱心的，都是由神而生，并且认识神。8没有爱心的，就不认识神，因为神就是爱。', null, '2017-04-02', null, 'A');
INSERT INTO `scripture` VALUES ('A93', '【约一4:10】不是我们爱神，乃是神爱我们，差他的儿子，为我们的罪作了挽回祭，这就是爱了。', null, '2017-04-03', null, 'A');
INSERT INTO `scripture` VALUES ('A94', '【约一4:18】爱里没有惧怕；爱既完全，就把惧怕除去，因为惧怕里含着刑罚，惧怕的人在爱里未得完全。', null, '2017-04-04', null, 'A');
INSERT INTO `scripture` VALUES ('A95', '【约一4:19】我们爱，因为神先爱我们。', null, '2017-04-05', null, 'A');
INSERT INTO `scripture` VALUES ('A96', '【约一4:21】爱神的，也当爱弟兄，这是我们从神所受的命令。', null, '2017-04-06', null, 'A');
INSERT INTO `scripture` VALUES ('A97', '【约一5:19】我们知道我们是属神的，全世界都卧在那恶者手下。', null, '2017-04-07', null, 'A');
INSERT INTO `scripture` VALUES ('A98', '【约二10-11】10若有人到你们那里，不是传这教训，不要接他到家里，也不要问他的安；11因为问他安的，就在他的恶行上有份。', null, '2017-04-08', null, 'A');
INSERT INTO `scripture` VALUES ('A99', '【约三2】亲爱的兄弟啊，我愿你凡事兴盛，身体健壮，正如你的灵魂兴盛一样。', null, '2017-04-09', null, 'A');
INSERT INTO `scripture` VALUES ('B1', '【创1:1】起初神创造天地。', null, '2017-05-03', null, 'B');
INSERT INTO `scripture` VALUES ('B10', '【创3:15】我又要叫你和女人彼此为仇；你的后裔和女人的后裔也彼此为仇。女人的后裔要伤你的头，你要伤他的脚跟。”', null, '2017-05-12', null, 'B');
INSERT INTO `scripture` VALUES ('B100', '【代上16:10-11】10要以他的圣名夸耀。寻求耶和华的人，心中应当欢喜。11要寻求耶和华与他的能力，时常寻求他的面。', null, '2017-08-10', null, 'B');
INSERT INTO `scripture` VALUES ('B101', '【代上16:26】外邦的神都属虚无，惟独耶和华创造诸天。', null, '2017-08-11', null, 'B');
INSERT INTO `scripture` VALUES ('B102', '【代上16:29】要将耶和华的名所当得的荣耀归给他，拿供物来奉到他面前；当以圣洁的妆饰（“的”或作“为”）敬拜耶和华。', null, '2017-08-12', null, 'B');
INSERT INTO `scripture` VALUES ('B103', '【代上16:34】应当称谢耶和华，因他本为善，他的慈爱永远长存。', null, '2017-08-13', null, 'B');
INSERT INTO `scripture` VALUES ('B104', '【代上28:9】“我儿所罗门哪，你当认识耶和华你父的神，诚心乐意地侍奉他;因为他鉴察众人的心，知道一切心思意念。你若寻求他，他必使你寻见；你若离弃他，他必永远丢弃你。', null, '2017-08-14', null, 'B');
INSERT INTO `scripture` VALUES ('B105', '【代上28:19】大卫说：“这一切工作的样式，都是耶和华用手划出来使我明白的。”', null, '2017-08-15', null, 'B');
INSERT INTO `scripture` VALUES ('B106', '【代上29:5】金子作金器，银子作银器，并籍匠人的手制造一切。今日有谁乐意将自己献给耶和华呢？”', null, '2017-08-16', null, 'B');
INSERT INTO `scripture` VALUES ('B107', '【代上29:14】“我算什么，我的民算什么，竟能如此乐意奉献？因为万物都从你而来，我们把从你而得的献给你！', null, '2017-08-17', null, 'B');
INSERT INTO `scripture` VALUES ('B108', '【代下6:14】说：“耶和华以色列的神啊，天上地下没有神可比你的。你向那尽心行在你面前的仆人守约施慈爱；', null, '2017-08-18', null, 'B');
INSERT INTO `scripture` VALUES ('B109', '【代下16:9】耶和华的眼目遍察全地，要显大能帮助向他心存诚实的人。', null, '2017-08-19', null, 'B');
INSERT INTO `scripture` VALUES ('B11', '【创3:19】你必汗流满面才得糊口，直到你归了土，因为你是从土而出的。你本是尘土，仍要归于尘土。”', null, '2017-05-13', null, 'B');
INSERT INTO `scripture` VALUES ('B110', '【代下19:7】现在你们应当敬畏耶和华，谨慎办事，因为耶和华我们的神没有不义，不偏待人，也不受贿赂。”', null, '2017-08-20', null, 'B');
INSERT INTO `scripture` VALUES ('B111', '【代下19:9】约沙法嘱咐他们说：“你们当敬畏耶和华，忠心诚实办事。', null, '2017-08-21', null, 'B');
INSERT INTO `scripture` VALUES ('B112', '【代下20:20】次日清早，众人起来往提哥亚的旷野去。出去的时候，约沙法站着说：“犹大人和耶路撒冷的居民哪，要听我说：信耶和华你们的神，就必立稳；信他的先知，就必亨通。”', null, '2017-08-22', null, 'B');
INSERT INTO `scripture` VALUES ('B113', '【代下36:15-16】15耶和华他们列祖的神，因为爱惜自己的民和他的居所，从早起来差遣使者去警戒他们。16他们却嘻笑神的使者，藐视他的言语，讥诮他的先知，以致耶和华的忿怒向他的百姓发作，无法可救。', null, '2017-08-23', null, 'B');
INSERT INTO `scripture` VALUES ('B114', '【拉6:22】欢欢喜喜地守除酵节七日，因为耶和华使他们欢喜，又使亚述王的心转向他们，坚固他们的手，作以色列神殿的工程。', null, '2017-08-24', null, 'B');
INSERT INTO `scripture` VALUES ('B115', '【拉7:6】这以斯拉从巴比伦上来，他是敏捷的文士，通达耶和华以色列神所赐摩西的律法书，王允准他一切所求的，是因耶和华他神的手帮助他。', null, '2017-08-25', null, 'B');
INSERT INTO `scripture` VALUES ('B116', '【拉7:10】以斯拉定志考究遵行耶和华的律法，又将律例典章教训以色列人。', null, '2017-08-26', null, 'B');
INSERT INTO `scripture` VALUES ('B117', '【拉7:27-28】27以斯拉说：“耶和华我们列祖的神是应当称颂的，因他使王起这心意修饰耶路撒冷耶和华的殿；28又在王和谋士，并大能的军长面前施恩于我。因耶和华我神的手帮助我，我就得以坚强，从以色列中招聚首领，与我一同上来。”', null, '2017-08-27', null, 'B');
INSERT INTO `scripture` VALUES ('B118', '【拉8:22】我求王拨步兵马兵帮助我们抵挡路上的仇敌，本以为羞耻，因我曾对王说：“我们神施恩的手，必帮助一切寻求他的；但他的能力和忿怒，必攻击一切离弃他的。”', null, '2017-08-28', null, 'B');
INSERT INTO `scripture` VALUES ('B119', '【拉9:15】耶和华以色列的神啊，因你是公义的，我们这剩下的人才得逃脱，正如今日的光景。看哪，我们在你面前有罪恶，因此无人在你面前站立得住。”', null, '2017-08-29', null, 'B');
INSERT INTO `scripture` VALUES ('B12', '【创3:20】亚当给他妻子起名叫夏娃，因为她是众生之母。', null, '2017-05-14', null, 'B');
INSERT INTO `scripture` VALUES ('B120', '【尼2:4】王问我说：“你要求什么？”于是我默祷天上的神。', null, '2017-08-30', null, 'B');
INSERT INTO `scripture` VALUES ('B121', '【尼8:10下】你们不要忧愁，因靠耶和华而得的喜乐是你们的力量。”', null, '2017-08-31', null, 'B');
INSERT INTO `scripture` VALUES ('B122', '【尼9:6】“你，惟独你，是耶和华。你造了天和天上的天，并天上的万象，地和地上的万物，海和海中所有的，这一切都是你所保存的；天军也都敬拜你。', null, '2017-09-01', null, 'B');
INSERT INTO `scripture` VALUES ('B123', '【斯2:15】末底改叔叔亚比孩的女儿，就是末底改收为自己女儿的以斯帖，按次序当进去见王的时候，除了掌管女子的太监希该所派定给她的，她别无所求。凡看见以斯帖的都喜悦她。', null, '2017-09-02', null, 'B');
INSERT INTO `scripture` VALUES ('B124', '【斯4:13-14】13末底改托人回复以斯帖说：“你莫想在王宫里强过一切犹大人，得免这祸。14此时你若闭口不言，犹大人必从别处得解脱、蒙拯救，你和你父家必至灭亡。焉知你得了王后的位分，不是为现今的机会吗？”', null, '2017-09-03', null, 'B');
INSERT INTO `scripture` VALUES ('B125', '【伯1:1】乌斯地有一个人，名叫约伯；那人完全正直，敬畏神，远离恶事。', null, '2017-09-04', null, 'B');
INSERT INTO `scripture` VALUES ('B126', '【伯1:20-21】20约伯便起来，撕裂外袍，剃了头，伏在地上下拜，21说：“我赤身出于母胎，也必赤身归回。赏赐的是耶和华，收取的也是耶和华；耶和华的名是应当称颂的。”', null, '2017-09-05', null, 'B');
INSERT INTO `scripture` VALUES ('B127', '【伯5:9】他行大事不可测度，行奇事不可胜数。', null, '2017-09-06', null, 'B');
INSERT INTO `scripture` VALUES ('B128', '【伯5:17-18】17“神所惩治的人是有福的，所以你不可轻看全能者的管教。18因为他打破，又缠裹；他击伤，用手医治。', null, '2017-09-07', null, 'B');
INSERT INTO `scripture` VALUES ('B129', '【伯19:25】我知道我的救赎主活着，末了必站立在地上。', null, '2017-09-08', null, 'B');
INSERT INTO `scripture` VALUES ('B13', '【创3:21】耶和华神为亚当和他妻子用皮子作衣服给他们穿。', null, '2017-05-15', null, 'B');
INSERT INTO `scripture` VALUES ('B130', '【伯22:21】“你要认识神，就得平安，福气也必临到你。', null, '2017-09-09', null, 'B');
INSERT INTO `scripture` VALUES ('B131', '【伯23:10】然而他知道我所行的路，他试炼我之后，我必如精金。', null, '2017-09-10', null, 'B');
INSERT INTO `scripture` VALUES ('B132', '【伯26:7-8】7神将北极铺在空中，将大地悬在虚空；8将水包在密云中，云却不破裂。', null, '2017-09-11', null, 'B');
INSERT INTO `scripture` VALUES ('B133', '【伯28:25-26】25要为风定轻重，又度量诸水。26他为雨露定命令，为雷电定道路。', null, '2017-09-12', null, 'B');
INSERT INTO `scripture` VALUES ('B134', '【伯28:28】他对人说：‘敬畏主就是智慧；远离恶便是聪明’”', null, '2017-09-13', null, 'B');
INSERT INTO `scripture` VALUES ('B135', '【伯33:4】神的灵造我，全能者的气使我得生。', null, '2017-09-14', null, 'B');
INSERT INTO `scripture` VALUES ('B136', '【伯33:28】神救赎我的灵魂免入深坑，我的生命也必见光。’', null, '2017-09-15', null, 'B');
INSERT INTO `scripture` VALUES ('B137', '【伯42:5-6】5我从前风闻有你，现在亲眼看见你。6因此我厌恶自己（“自己”或作“我的言语”），在尘土和炉灰中懊悔。”', null, '2017-09-16', null, 'B');
INSERT INTO `scripture` VALUES ('B138', '【伯42:10】约伯为他的朋友祈祷，耶和华就使约伯从苦境（\"苦境\"原文作\"掳掠\"）转回，并且耶和华赐给他的比他从前所有的加倍。', null, '2017-09-17', null, 'B');
INSERT INTO `scripture` VALUES ('B139', '【诗1:1-3】1不从恶人的计谋，不站罪人的道路，不坐亵慢人的座位，2惟喜爱耶和华的律法，昼夜思想，这人便为有福。3他要像一棵树栽在溪水旁，按时候结果子，叶子也不枯干。凡他所作的尽都顺利。', null, '2017-09-18', null, 'B');
INSERT INTO `scripture` VALUES ('B14', '【创5:24】以诺与神同行，神将他取去，他就不在世了。', null, '2017-05-16', null, 'B');
INSERT INTO `scripture` VALUES ('B140', '【诗3:3】但你耶和华是我四围的盾牌，是我的荣耀，又是叫我抬起头来的。', null, '2017-09-19', null, 'B');
INSERT INTO `scripture` VALUES ('B141', '【诗5:3】耶和华啊，早晨你必听我的声音；早晨我必向你陈明我的心意，并要警醒。', null, '2017-09-20', null, 'B');
INSERT INTO `scripture` VALUES ('B142', '【诗8:1-2】1（大卫的诗，交与伶长。用迦特乐器。）耶和华我们的主啊，你的名在全地何其美！你将你的荣耀彰显于天。2你因敌人的缘故，从婴孩和吃奶的口中建立了能力，使仇敌和报仇的闭口无言。', null, '2017-09-21', null, 'B');
INSERT INTO `scripture` VALUES ('B143', '【诗8:3-5】3我观看你指头所造的天，并你所陈设的月亮星宿，4便说，人算什么，你竟顾念他？世人算什么，你竟眷顾他？5你叫他比天使（或作“神”）微小一点，并赐他荣耀尊贵为冠冕。', null, '2017-09-22', null, 'B');
INSERT INTO `scripture` VALUES ('B144', '【诗12:6】耶和华的言语是纯净的言语，如同银子在泥炉中炼过七次。', null, '2017-09-23', null, 'B');
INSERT INTO `scripture` VALUES ('B145', '【诗16:2】我的心哪，你曾对耶和华说：“你是我的主，我的好处不在你以外。”', null, '2017-09-24', null, 'B');
INSERT INTO `scripture` VALUES ('B146', '【诗16:8-9】8我将耶和华常摆在我面前，因他在我右边，我便不至摇动。9因此，我的心欢喜，我的灵（原文作“荣耀”）快乐，我的肉身也要安然居住。', null, '2017-09-25', null, 'B');
INSERT INTO `scripture` VALUES ('B147', '【诗19:1】诸天述说神的荣耀，穹苍传扬他的手段。', null, '2017-09-26', null, 'B');
INSERT INTO `scripture` VALUES ('B148', '【诗19:7】耶和华的律法全备，能苏醒人心；耶和华的法度确定，能使愚人有智慧；', null, '2017-09-27', null, 'B');
INSERT INTO `scripture` VALUES ('B149', '【诗19:14】耶和华我的磐石，我的救赎主啊，愿我口中的言语，心里的意念，在你面前蒙悦纳。', null, '2017-09-28', null, 'B');
INSERT INTO `scripture` VALUES ('B15', '【创5:27】玛土撒拉共活了九百六十九岁就死了。', null, '2017-05-17', null, 'B');
INSERT INTO `scripture` VALUES ('B150', '【诗20:7】有人靠车，有人靠马，但我们要提到耶和华我们神的名。', null, '2017-09-29', null, 'B');
INSERT INTO `scripture` VALUES ('B151', '【诗22:3】但你是圣洁的，是用以色列的赞美为宝座（“宝座”或作“居所”）的。', null, '2017-09-30', null, 'B');
INSERT INTO `scripture` VALUES ('B152', '【诗22:18】他们分我的外衣，为我的里衣拈阄。', null, '2017-10-01', null, 'B');
INSERT INTO `scripture` VALUES ('B153', '【诗23:1-3】1（大卫的诗。）耶和华是我的牧者，我必不至缺乏。2他使我躺卧在青草地上，领我在可安歇的水边；3他使我的灵魂苏醒，为自己的名引导我走义路。', null, '2017-10-02', null, 'B');
INSERT INTO `scripture` VALUES ('B154', '【诗23:4】我虽然行过死荫的幽谷，也不怕遭害，因为你与我同在；你的杖，你的竿，都安慰我。', null, '2017-10-03', null, 'B');
INSERT INTO `scripture` VALUES ('B155', '【诗23:5-6】5在我敌人面前，你为我摆设筵席；你用油膏了我的头，使我的福杯满溢。6我一生一世必有恩惠慈爱随着我，我且要住在耶和华的殿中，直到永远。', null, '2017-10-04', null, 'B');
INSERT INTO `scripture` VALUES ('B156', '【诗24:3-4】3谁能登耶和华的山？谁能站在他的圣所？4就是手洁心清，不向虚妄，起誓不怀诡诈的人。', null, '2017-10-05', null, 'B');
INSERT INTO `scripture` VALUES ('B157', '【诗25:5】求你以你的真理引导我，教训我，因为你是救我的神，我终日等候你。', null, '2017-10-06', null, 'B');
INSERT INTO `scripture` VALUES ('B158', '【诗25:12】谁敬畏耶和华，耶和华必指示他当选择的道路。', null, '2017-10-07', null, 'B');
INSERT INTO `scripture` VALUES ('B159', '【诗27:1】（大卫的诗。）耶和华是我的亮光，是我的拯救，我还怕谁呢？耶和华是我性命的保障（“保障”或作“力量”），我还惧谁呢？', null, '2017-10-08', null, 'B');
INSERT INTO `scripture` VALUES ('B16', '【创6:9】挪亚的后代记在下面。挪亚是个义人，在当时的世代是个完全人。挪亚与神同行。', null, '2017-05-18', null, 'B');
INSERT INTO `scripture` VALUES ('B160', '【诗27:4】有一件事，我曾求耶和华，我仍要寻求：就是一生一世住在耶和华的殿中，瞻仰他的荣美，在他的殿里求问。', null, '2017-10-09', null, 'B');
INSERT INTO `scripture` VALUES ('B161', '【诗28:7】耶和华是我的力量，是我的盾牌，我心里倚靠他，就得帮助；所以我心中欢乐，我必用歌颂赞他。', null, '2017-10-10', null, 'B');
INSERT INTO `scripture` VALUES ('B162', '【诗30:5】因为，他的怒气不过是转眼之间；他的恩典乃是一生之久。一宿虽然有哭泣，早晨便必欢呼。', null, '2017-10-11', null, 'B');
INSERT INTO `scripture` VALUES ('B163', '【诗30:11-12】11你已将我的哀哭变为跳舞，将我的麻衣脱去，给我披上喜乐。12好叫我的灵（原文作“荣耀”）歌颂你，并不住声。耶和华我的神啊，我要称谢你，直到永远。', null, '2017-10-12', null, 'B');
INSERT INTO `scripture` VALUES ('B164', '【诗32:1】（大卫的训诲诗。）得赦免其过、遮盖其罪的，这人是有福的。', null, '2017-10-13', null, 'B');
INSERT INTO `scripture` VALUES ('B165', '【诗33:6】诸天藉耶和华的命而造，万象藉他口中的气而成。', null, '2017-10-14', null, 'B');
INSERT INTO `scripture` VALUES ('B166', '【诗33:9】因为他说有，就有；命立，就立。', null, '2017-10-15', null, 'B');
INSERT INTO `scripture` VALUES ('B167', '【诗33:12】以耶和华为神的，那国是有福的！他所拣选为自己产业的，那民是有福的！', null, '2017-10-16', null, 'B');
INSERT INTO `scripture` VALUES ('B168', '【诗34:1】我要时时称颂耶和华，赞美他的话必常在我口中。', null, '2017-10-17', null, 'B');
INSERT INTO `scripture` VALUES ('B169', '【诗34:7】耶和华的使者，在敬畏他的人四围安营搭救他们。', null, '2017-10-18', null, 'B');
INSERT INTO `scripture` VALUES ('B17', '【创8:4】七月十七日，方舟停在亚拉腊山上。', null, '2017-05-19', null, 'B');
INSERT INTO `scripture` VALUES ('B170', '【诗34:8】你们要尝尝主恩的滋味，便知道他是美善，投靠他的人有福了！', null, '2017-10-19', null, 'B');
INSERT INTO `scripture` VALUES ('B171', '【诗34:9-10】9耶和华的圣民哪，你们当敬畏他，因敬畏他的一无所缺。10少壮狮子还缺食忍饿；但寻求耶和华的，什么好处都不缺。', null, '2017-10-20', null, 'B');
INSERT INTO `scripture` VALUES ('B172', '【诗34:18】耶和华靠近伤心的人，拯救灵性痛悔的人。', null, '2017-10-21', null, 'B');
INSERT INTO `scripture` VALUES ('B173', '【诗36:5】耶和华啊，你的慈爱上及诸天；你的信实达到穹苍。', null, '2017-10-22', null, 'B');
INSERT INTO `scripture` VALUES ('B174', '【诗39:9】因我所遭遇的是出于你，我就默然不语。', null, '2017-10-23', null, 'B');
INSERT INTO `scripture` VALUES ('B175', '【诗42:1】（可拉后裔的训诲诗，交与伶长。）神啊，我的心切慕你，如鹿切慕溪水。', null, '2017-10-24', null, 'B');
INSERT INTO `scripture` VALUES ('B176', '【诗46:1】（可拉后裔的诗歌，交与伶长。调用女音。）神是我们的避难所，是我们的力量，是我们在患难中随时的帮助。', null, '2017-10-25', null, 'B');
INSERT INTO `scripture` VALUES ('B177', '【诗51:1-2】1（大卫与拔示巴同室以后，先知拿单来见他。他作这诗，交与伶长。）神啊，求你按你的慈爱怜恤我，按你丰盛的慈悲涂抹我的过犯。2求你将我的罪孽洗除净尽，并洁除我的罪。', null, '2017-10-26', null, 'B');
INSERT INTO `scripture` VALUES ('B178', '【诗51:5】我是在罪孽里生的，在我母亲怀胎的时候就有了罪。', null, '2017-10-27', null, 'B');
INSERT INTO `scripture` VALUES ('B179', '【诗51:6】你所喜爱的是内里诚实；你在我隐密处必使我得智慧。', null, '2017-10-28', null, 'B');
INSERT INTO `scripture` VALUES ('B18', '【创15:6】亚伯兰信耶和华，耶和华就以此为他的义。', null, '2017-05-20', null, 'B');
INSERT INTO `scripture` VALUES ('B180', '【诗51:10】神啊，求你为我造清洁的心，使我里面重新有正直的灵（“正直”或作“坚定”）。', null, '2017-10-29', null, 'B');
INSERT INTO `scripture` VALUES ('B181', '【诗51:17】神所要的祭，就是忧伤的灵。神啊，忧伤痛悔的心，你必不轻看。', null, '2017-10-30', null, 'B');
INSERT INTO `scripture` VALUES ('B182', '【诗56:3-4】3我惧怕的时候要倚靠你。4我倚靠神，我要赞美他的话；我倚靠神，必不惧怕。血气之辈能把我怎么样呢？', null, '2017-10-31', null, 'B');
INSERT INTO `scripture` VALUES ('B183', '【诗57:8】我的灵啊（原文作“荣耀”），你当醒起！琴瑟啊，你们当醒起！我自己要极早醒起。', null, '2017-11-01', null, 'B');
INSERT INTO `scripture` VALUES ('B184', '【诗57:9-10】9主啊，我要在万民中称谢你，在列邦中歌颂你；10因为你的慈爱高及诸天，你的诚实达到穹苍。', null, '2017-11-02', null, 'B');
INSERT INTO `scripture` VALUES ('B185', '【诗63:1】（大卫在犹大旷野的时候，作了这诗。）神啊，你是我的神。我要切切地寻求你；在干旱疲乏无水之地，我渴想你，我的心切慕你。', null, '2017-11-03', null, 'B');
INSERT INTO `scripture` VALUES ('B186', '【诗65:11】你以恩典为年岁的冠冕，你的路径都滴下脂油，', null, '2017-11-04', null, 'B');
INSERT INTO `scripture` VALUES ('B187', '【诗68:19】天天背负我们重担的主，就是拯救我们的神，是应当称颂的。（细拉）', null, '2017-11-05', null, 'B');
INSERT INTO `scripture` VALUES ('B188', '【诗73:25-26】25除你以外，在天上我有谁呢？除你以外，在地上我也没有所爱慕的。26我的肉体和我的心肠衰残，但神是我心里的力量，又是我的福分，直到永远。', null, '2017-11-06', null, 'B');
INSERT INTO `scripture` VALUES ('B189', '【诗88:13】耶和华啊，我呼求你，我早晨的祷告要达到你面前。', null, '2017-11-07', null, 'B');
INSERT INTO `scripture` VALUES ('B19', '【创19:26】罗得的妻子在后边回头一看，就变成了一根盐柱。', null, '2017-05-21', null, 'B');
INSERT INTO `scripture` VALUES ('B190', '【诗89:1】（以斯拉人以探的训诲诗。）我要歌唱耶和华的慈爱，直到永远。我要用口将你的信实传与万代。', null, '2017-11-08', null, 'B');
INSERT INTO `scripture` VALUES ('B191', '【诗90:10】我们一生的年日是七十岁，若是强壮可到八十岁；但其中所矜夸的不过是劳苦愁烦，转眼成空，我们便如飞而去。', null, '2017-11-09', null, 'B');
INSERT INTO `scripture` VALUES ('B192', '【诗90:12】求你指教我们怎样数算自己的日子，好叫我们得着智慧的心。', null, '2017-11-10', null, 'B');
INSERT INTO `scripture` VALUES ('B193', '【诗92:12】义人要发旺如棕树，生长如黎巴嫩的香柏树。', null, '2017-11-11', null, 'B');
INSERT INTO `scripture` VALUES ('B194', '【诗96:5】外邦的神都属虚无，惟独耶和华创造诸天。', null, '2017-11-12', null, 'B');
INSERT INTO `scripture` VALUES ('B195', '【诗100:3】你们当晓得耶和华是神。我们是他造的，也是属他的；我们是他的民，也是他草场的羊。', null, '2017-11-13', null, 'B');
INSERT INTO `scripture` VALUES ('B196', '【诗103:2-3】2我的心哪，你要称颂耶和华，不可忘记他的一切恩惠。3他赦免你的一切罪孽，医治你的一切疾病。', null, '2017-11-14', null, 'B');
INSERT INTO `scripture` VALUES ('B197', '【诗103:11-12】11天离地何等的高，他的慈爱向敬畏他的人也是何等的大；12东离西有多远，他叫我们的过犯离我们也有多远。', null, '2017-11-15', null, 'B');
INSERT INTO `scripture` VALUES ('B198', '【诗110:1】（大卫的诗。）耶和华对我主说：“你坐在我的右边，等我使你仇敌作你的脚凳。”', null, '2017-11-16', null, 'B');
INSERT INTO `scripture` VALUES ('B199', '【诗113:1】你们要赞美耶和华！耶和华的仆人哪，你们要赞美，赞美耶和华的名。', null, '2017-11-17', null, 'B');
INSERT INTO `scripture` VALUES ('B2', '【创1:3】神说：“要有光”，就有了光。', null, '2017-05-04', null, 'B');
INSERT INTO `scripture` VALUES ('B20', '【创22:14】亚伯拉罕给那地方起名叫耶和华以勒（意思就是“耶和华必预备”），直到今日人还说：“在耶和华的山上必有预备。”', null, '2017-05-22', null, 'B');
INSERT INTO `scripture` VALUES ('B200', '【诗113:2】耶和华的名是应当称颂的，从今时直到永远。', null, '2017-11-18', null, 'B');
INSERT INTO `scripture` VALUES ('B201', '【诗113:3】从日出之地到日落之处，耶和华的名是应当赞美的。', null, '2017-11-19', null, 'B');
INSERT INTO `scripture` VALUES ('B202', '【诗113:4】耶和华超乎万民之上，他的荣耀高过诸天。', null, '2017-11-20', null, 'B');
INSERT INTO `scripture` VALUES ('B203', '【诗113:5-6】5谁像耶和华我们的神呢？他坐在至高之处，6自己谦卑，观看天上地下的事。', null, '2017-11-21', null, 'B');
INSERT INTO `scripture` VALUES ('B204', '【诗113:7-8】7他从灰尘里抬举贫寒人，从粪堆中提拔穷乏人，8使他们与王子同坐，就是与本国的王子同坐。', null, '2017-11-22', null, 'B');
INSERT INTO `scripture` VALUES ('B205', '【诗113:9】他使不能生育的妇人安居家中，为多子的乐母。你们要赞美耶和华！', null, '2017-11-23', null, 'B');
INSERT INTO `scripture` VALUES ('B206', '【诗116:8】主啊，你救我的命免了死亡，救我的眼免了流泪，救我的脚免了跌倒；', null, '2017-11-24', null, 'B');
INSERT INTO `scripture` VALUES ('B207', '【诗116:12-13】12我拿什么报答耶和华向我所赐的一切厚恩？13我要举起救恩的杯，称扬耶和华的名。', null, '2017-11-25', null, 'B');
INSERT INTO `scripture` VALUES ('B208', '【诗118:6】有耶和华帮助我，我必不惧怕，人能把我怎么样呢？', null, '2017-11-26', null, 'B');
INSERT INTO `scripture` VALUES ('B209', '【诗118:8-9】8投靠耶和华，强似倚赖人；9投靠耶和华，强似倚赖王子。', null, '2017-11-27', null, 'B');
INSERT INTO `scripture` VALUES ('B21', '【创25:26】随后又生了以扫的兄弟，手抓住以扫的脚跟，因此给他起名叫雅各（“雅各”就是“抓住”的意思）。利百加生下两个儿子的时候，以撒年正六十岁。', null, '2017-05-23', null, 'B');
INSERT INTO `scripture` VALUES ('B210', '【诗119:9】少年人用什么洁净他的行为呢？是要遵行你的话。', null, '2017-11-28', null, 'B');
INSERT INTO `scripture` VALUES ('B211', '【诗119:11】我将你的话藏在心里，免得我得罪你。', null, '2017-11-29', null, 'B');
INSERT INTO `scripture` VALUES ('B212', '【诗119:18】求你开我的眼睛，使我看出你律法中的奇妙。', null, '2017-11-30', null, 'B');
INSERT INTO `scripture` VALUES ('B213', '【诗119:60】我急忙遵守你的命令，并不迟延。', null, '2017-12-01', null, 'B');
INSERT INTO `scripture` VALUES ('B214', '【诗119:67】我未受苦以先走迷了路，现在却遵守你的话。', null, '2017-12-02', null, 'B');
