INSERT INTO `scripture` VALUES ('B60', '【申6:6-9】6我今日所吩咐你的话都要记在心上，7也要殷勤教训你的儿女，无论你坐在家里，行在路上，躺下，起来，都要谈论；8也要系在手上为记号，戴在额上为经文；9又要写在你房屋的门框上，并你的城门上。”', null, '2017-07-01', null, 'B');
