INSERT INTO `scripture` VALUES ('B64', '【申10:12-13】12“以色列啊，现在耶和华你神向你所要的是什么呢？只要你敬畏耶和华你的神，遵行他的道，爱他，尽心尽性侍奉他，13遵守他的诫命、律例，就是我今日所吩咐你的，为要叫你得福。', null, '2017-07-05', null, 'B');
